INSERT INTO `pa_forum` VALUES ('2', '0', 'Java', '', '4', '利用Python对web网站的开发', '', '0.00', '7', '1545286929', '', '', '', '/public/static/images/tea_column_default.jpg', '1');
INSERT INTO `pa_forum` VALUES ('3', '0', 'Web', '0', '2', '在运维的工作中，有大量重复性工作的地方，并需要做管理系统、监控系统、发布系统等，将工作自动化起来，提高工作效率，这样的场景Python是一门非常合适的语言。', '', '0.00', '6', '1545287105', '', '', '', '/public/static/images/tea_column_default.jpg', '1');
INSERT INTO `pa_forum` VALUES ('4', '0', 'Python', '1,0', '0', 'Python被广泛的运用于科学和数字计算中，例如生物信息学、物理、建筑、地理信息系统、图像可视化分析、生命科学等，常用numpy、SciPy、Biopython、SunPy等。', '', '0.00', '5', '1545287162', '', '', '', '/public/static/images/tea_column_default.jpg', '1');
INSERT INTO `pa_forum` VALUES ('5', '0', 'UI', '', '0', '网络编程是Python学习的另一方向，网络编程在生活和开发中无处不在，哪里有通讯就有网络，它可以称为是一切开发的“基石”。对于所有编程开发人员必须要知其然并知其所以然，所以网络部分将从协议、封包、解包等底层进行深入剖析。', '', '0.00', '4', '1546412939', '', '', '', '/public/static/images/tea_column_default.jpg', '1');
INSERT INTO `pa_forum` VALUES ('6', '0', '大数据', '0', '0', '在爬虫领域，Python几乎是霸主地位，将网络一切数据作为资源，通过自动化程序进行有针对性的数据采集以及处理。从事该领域应学习爬虫策略、高性能异步IO、分布式爬虫等，并针对Scrapy框架源码进行深入剖析，从而理解其原理并实现自定义爬虫框架。', '', '0.00', '3', '1546412993', '', '', '', '/public/static/images/tea_column_default.jpg', '1');
INSERT INTO `pa_forum` VALUES ('7', '6', '云计算', '1', '0', '云计算描述', '', '0.00', '0', '1546413125', '', '', '', '/public/static/images/tea_column_default.jpg', '1');
INSERT INTO `pa_forum` VALUES ('8', '0', 'Android', '', '0', '金融分析包含金融知识和Python相关模块的学习，学习内容囊括Numpy\\Pandas\\Scipy数据分析模块等，以及常见金融分析策略如“双均线”、“周规则交易”、“羊驼策略”、“Dual Thrust 交易策略”等。', '', '0.00', '2', '1546413165', '', '', '', '/public/static/images/tea_column_default.jpg', '1');
INSERT INTO `pa_forum` VALUES ('9', '0', '人工智能', '0', '0', '在网络游戏开发中，Python也有很多应用，相比于Lua or C++，Python比Lua有更高阶的抽象能力，可以用更少的代码描述游戏业务逻辑，Python非常适合编写1万行以上的项目，而且能够很好的把网游项目的规模控制在10万行代码以内。 ', '', '0.00', '1', '1546413192', '', '', '', '/public/static/images/tea_column_default.jpg', '1');
