INSERT INTO `pa_topic` VALUES ('31', '36', '4', 'LK796c8d1XjS5FfaheJOpbZwWRH4uq', '121.229.180.134', '配置springmvc.xml', '1553654604', '1553654604', '&lt;p&gt;本节操作完成了对spring子容器的配置。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;1、创建源代码和配置文件所在目录&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;创建java文件夹，设置为Sources Root，具体操作如下图所示：&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190327/1553654531148770.gif&quot; title=&quot;1553654531148770.gif&quot; alt=&quot;1553654531148770.gif&quot; width=&quot;800&quot; height=&quot;515&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;2、创建各层package&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;创建controller&lt;/p&gt;&lt;p&gt;创建service&lt;/p&gt;&lt;p&gt;创建mapper&lt;/p&gt;&lt;p&gt;创建entity&lt;/p&gt;&lt;p&gt;具体操作如下图所示：&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190327/1553654551869176.gif&quot; title=&quot;1553654551869176.gif&quot; alt=&quot;1553654551869176.gif&quot; width=&quot;800&quot; height=&quot;532&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;3、在resources中创建springmvc.xml&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;具体操作如下图所示：&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190327/1553654570377778.gif&quot; title=&quot;1553654570377778.gif&quot; alt=&quot;1553654570377778.gif&quot; width=&quot;800&quot; height=&quot;532&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;4、修改springmvc.xml&lt;/strong&gt;&lt;/p&gt;&lt;pre class=&quot;brush:xml;toolbar:false&quot;&gt;&amp;lt;?xml&amp;nbsp;version=&amp;quot;1.0&amp;quot;&amp;nbsp;encoding=&amp;quot;UTF-8&amp;quot;?&amp;gt;\n&amp;lt;beans&amp;nbsp;xmlns=&amp;quot;http://www.springframework.org/schema/beans&amp;quot;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;xmlns:xsi=&amp;quot;http://www.w3.org/2001/XMLSchema-instance&amp;quot;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;xmlns:context=&amp;quot;http://www.springframework.org/sche