INSERT INTO `pa_topic` VALUES ('60', '39', '1', 'vESWwzMGU5uQtch24msAVp1n9jxJKO', '180.111.167.141', '内嵌框架', '1554859370', '1554859370', '&lt;h3 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;HTML内嵌框架&lt;/a&gt;&lt;/h3&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&amp;lt;iframe&amp;gt;&lt;span style=&quot;font-family:宋体&quot;&gt;标签会创建包含另外一个&lt;/span&gt;html&lt;span style=&quot;font-family:宋体&quot;&gt;文件的内联框架（即行内框架），&lt;/span&gt;src&lt;span style=&quot;font-family:宋体&quot;&gt;属性来定义另一个&lt;/span&gt;html&lt;span style=&quot;font-family:宋体&quot;&gt;文件的引用地址，&lt;/span&gt;frameborder&lt;span style=&quot;font-family:宋体&quot;&gt;属性定义边框，&lt;/span&gt;scrolling&lt;span style=&quot;font-family:宋体&quot;&gt;属性定义是否有滚动条，代码如下：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:html;toolbar:false&quot;&gt;&amp;lt;iframe&amp;nbsp;src=&amp;quot;http://www...&amp;quot;&amp;nbsp;frameborder=&amp;quot;0&amp;quot;&amp;nbsp;scrolling=&amp;quot;no&amp;quot;&amp;gt;&amp;lt;/iframe&amp;gt;&lt;/pre&gt;&lt;h3 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;内嵌框架与a标签配合使用&lt;/a&gt;&lt;/h3&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;a&lt;span style=&quot;font-family:宋体&quot;&gt;标签的&lt;/span&gt;target&lt;span style=&quot;font-family:宋体&quot;&gt;属性可以将链接到的页面直接显示在当前页面的&lt;/span&gt;iframe&lt;span style=&quot;font-family:宋体&quot;&gt;中，代码如下：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:html;toolbar:false&quot;&gt;&amp;lt;a&amp;nbsp;href=&amp;quot;01.html&amp;quot;&amp;nbsp;target=&amp;quot;myframe&amp;quot;&amp;gt;页面一&amp;lt;/a&amp;gt;\n&amp;nbsp;&amp;lt;a&amp;nbsp;href=&amp;quot;02.html&amp;quot;&amp;nbsp;target=&amp;quot;myframe&amp;quot;&amp;gt;页面二&amp;lt;/a&amp;gt;\n&amp;nbsp;&amp;lt;a&amp;nbsp;href=&amp;quot;03.html&amp;quot;&amp;nbsp;target=&amp;quot;myframe&amp;quot;&amp;gt;页面三&amp;lt;/a&amp;gt;\n&amp;nbsp;&amp;lt;iframe&amp;nbsp;src=&amp;quot;01.html&amp;quot;&amp;nbsp;frameborder=&amp;quot;0&amp;quot;&amp;nbsp;scrolling=&amp;quot;no&amp;quot;&amp;nbsp;name=&amp;quot;myframe&amp;quot;&amp;gt;&amp;lt;/iframe&amp;gt;&lt;/pre&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;示例：&lt;/span&gt;08-&lt;span style=&quot;font-family:宋体&quot;&gt;内嵌框架&lt;/span&gt;.html&lt;/p&gt;&lt;pre class=&quot;brush:html;toolbar:false&quot;&gt;&amp;lt;html&amp;gt;\n&amp;nbsp;\n&amp;nbsp;&amp;lt;head&amp;gt;\n&amp;nbsp;\n&amp;nbsp;&amp;lt;/head&amp;gt;\n&amp;nbsp;\n&amp;nbsp;&amp;lt;body&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;a&amp;nbsp;href=&amp;quot;01-文本内容.html&amp;quot;&amp;nbsp;target=&amp;quot;myframe&amp;quot;&amp;gt;页面一&amp;lt;/a&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;a&amp;nbsp;href=&amp;quot;02-强调性标签.html&amp;quot;&amp;nbsp;target=&amp;quot;myframe&amp;quot;&amp;gt;页面二&amp;lt;/a&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;a&amp;nbsp;href=&amp;quot;03-img标签.html&amp;quot;&amp;nbsp;target=&amp;quot;myframe&amp;quot;&amp;gt;页面三&amp;lt;/a&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;br&amp;nbsp;/&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;iframe&amp;nbsp;src=&amp;quot;01-文本内容.html&amp;quot;&amp;nbsp;frameborder=&amp;quot;0&amp;quot;&amp;nbsp;scrolling=&amp;quot;no&amp;quot;&amp;nbsp;name=&amp;quot;myframe&amp;quot;&amp;gt;&amp;lt;/iframe&amp;gt;\n&amp;nbsp;&amp;lt;/body&amp;gt;\n&amp;nbsp;\n&amp;nbsp;&amp;lt;/html&amp;gt;&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '5', '0', '0', '0', '0', '0', '0');
INSERT INTO `pa_topic` VALUES ('61', '39', '1', 'gCwyLUbex6R7V0dX9hrzEo5ZQvcNJp', '180.111.167.141', '基本语法及引用', '1554859454', '1554859454', '&lt;h3 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;CSS基本语法&lt;/a&gt;&lt;/h3&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;CSS&lt;span style=&quot;font-family:宋体&quot;&gt;的定义方法是：&lt;/span&gt;&lt;/p&gt;&lt;h4 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;选择器 { 属性:值; 属性:值; 属性:值;}&lt;/a&gt;&lt;/h4&gt;&lt;p&gt;&lt;span style=&quot;font-size:14px;font-family:宋体&quot;&gt;选择器是将样式和页面元素关联起来的名称，属性是希望设置的样式属性每个属性有一个或多个值。代码示例：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:css;toolbar:false&quot;&gt;div{&amp;nbsp;width:100px;&amp;nbsp;height:100px;&amp;nbsp;color:red&amp;nbsp;}&lt;/pre&gt;&lt;h3 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;CSS页面引入方法：&lt;/a&gt;&lt;/h3&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;1&lt;span style=&quot;font-family:宋体&quot;&gt;、外联式：通过&lt;/span&gt;link&lt;span style=&quot;font-family:宋体&quot;&gt;标签，链接到外部样式表到页面中。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;2&lt;span style=&quot;font-family:宋体&quot;&gt;、嵌入式：通过&lt;/span&gt;style&lt;span style=&quot;font-family:宋体&quot;&gt;标签，在网页上创建嵌入的样式表。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;3&lt;span style=&quot;font-family:宋体&quot;&gt;、内联式：通过标签的&lt;/span&gt;style&lt;span style=&quot;font-family:宋体&quot;&gt;属性，在标签上直接写样式。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;示例：&lt;/span&gt;01-&lt;span style=&quot;font-family:宋体&quot;&gt;基本语法举例&lt;/span&gt;.html&lt;/p&gt;&lt;pre class=&quot;brush:html;toolbar:false&quot;&gt;&amp;lt;!DOCTYPE&amp;nbsp;html&amp;gt;\n&amp;nbsp;&amp;lt;html&amp;gt;\n&amp;nbsp;\n&amp;nbsp;&amp;lt;head&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;meta&amp;nbsp;charset=&amp;quot;UTF-8&amp;quot;&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;meta&amp;nbsp;name=&amp;quot;viewport&amp;quot;&amp;nbsp;content=&amp;quot;width=&amp;lt;device-width&amp;gt;,&amp;nbsp;initial-scale=1.0&amp;quot;&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;meta&amp;nbsp;http-equiv=&amp;quot;X-UA-Compatible&amp;quot;&amp;nbsp;content=&amp;quot;ie=edge&amp;quot;&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;title&amp;gt;Document&amp;lt;/title&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;style&amp;nbsp;type=&amp;quot;text/css&amp;quot;&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;div&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;width:&amp;nbsp;100px;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;height:&amp;nbsp;100px;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;color:&amp;nbsp;blue;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/style&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;!--&amp;nbsp;&amp;lt;link&amp;nbsp;rel=&amp;quot;stylesheet&amp;quot;&amp;nbsp;type=&amp;quot;text/css&amp;quot;&amp;nbsp;href=&amp;quot;css/01.css&amp;quot;&amp;gt;&amp;nbsp;--&amp;gt;\n&amp;nbsp;&amp;lt;/head&amp;gt;\n&amp;nbsp;\n&amp;nbsp;&amp;lt;body&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;!--&amp;nbsp;&amp;lt;div&amp;nbsp;style=&amp;quot;width:100px;&amp;nbsp;height:100px;&amp;nbsp;color:yellow;&amp;quot;&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;内联式举例\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/div&amp;gt;&amp;nbsp;--&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;div&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;嵌入式举例\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/div&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;!--&amp;nbsp;&amp;lt;div&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;外联式举例\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/div&amp;gt;&amp;nbsp;--&amp;gt;\n&amp;nbsp;&amp;lt;/body&amp;gt;\n&amp;nbsp;\n&amp;nbsp;&amp;lt;/html&amp;gt;&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '5', '0', '0', '0', '0', '0', '0');
INSERT INTO `pa_topic` VALUES ('94', '40', '1', 'Nqz57yGdFMC8taQSkRVKYi6lBUHWmE', '180.111.167.141', '完整性约束', '1554966731', '1554966731', '&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;完整性约束是关系数据库中的对象，用以存放插入到一个表某一列的规则。强制数据完整性可确保数据库中的数据质量。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;对表进行计划有两个重要步骤：列的有效值和确定如何强制列中的数据完整性。&lt;/span&gt;&lt;/p&gt;&lt;h3 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;数据完整性有四种类型：&lt;/span&gt;&lt;/a&gt;&lt;/h3&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font:9px &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;实体完整性&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font:9px &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;域完整性&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font:9px &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;引用完整性&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font:9px &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;用户定义完整性&lt;/span&gt;&lt;/p&gt;&lt;h4 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;实体完整性&lt;/span&gt;&lt;/a&gt;&lt;/h4&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;实体完整性将行定义为特定表的唯一实体。实体完整性强制要求表的标识符列或主键的完整性（通过索引、&lt;/span&gt;UNIQUE &lt;span style=&quot;font-family:宋体&quot;&gt;约束、&lt;/span&gt;PRIMARY KEY &lt;span style=&quot;font-family:宋体&quot;&gt;约束或&lt;/span&gt; IDENTITY &lt;span style=&quot;font-family:宋体&quot;&gt;属性）。&lt;/span&gt;&lt;/p&gt;&lt;h4 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;域完整性&lt;/span&gt;&lt;/a&gt;&lt;/h4&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;域完整性是指给定列的输入有效性。强制域有效性的方法有：限制类型（通过数据类型）、格式（通过&lt;/span&gt; CHECK &lt;span style=&quot;font-family:宋体&quot;&gt;约束和规则）或可能值的范围（通过&lt;/span&gt; FOREIGN KEY &lt;span style=&quot;font-family:宋体&quot;&gt;约束、&lt;/span&gt;CHECK &lt;span style=&quot;font-family:宋体&quot;&gt;约束、&lt;/span&gt;DEFAULT &lt;span style=&quot;font-family:宋体&quot;&gt;定义、&lt;/span&gt;NOT NULL &lt;span style=&quot;font-family:宋体&quot;&gt;定义和规则）。&lt;/span&gt;&lt;/p&gt;&lt;h4 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;引用完整性&lt;/span&gt;&lt;/a&gt;&lt;/h4&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;在输入或删除记录时，引用完整性保持表之间已定义的关系。引用完整性基于外键与主键之间或外键与唯一键之间的关系（通过&lt;/span&gt; FOREIGN KEY &lt;span style=&quot;font-family:宋体&quot;&gt;和&lt;/span&gt; CHECK &lt;span style=&quot;font-family:宋体&quot;&gt;约束）。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;引用完整性确保键值在所有表中一致。这样的一致性要求不能引用不存在的值，如果键值更改了，那么在整个数据库中，对该键值的所有引用要进行一致的更改。&lt;/span&gt;&lt;/p&gt;&lt;h4 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;用户定义完整性&lt;/span&gt;&lt;/a&gt;&lt;/h4&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;用户定义完整性使您得以定义不属于其它任何完整性分类的特定业务规则。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;所有的完整性类型都支持用户定义完整性（&lt;/span&gt;CREATE TABLE &lt;span style=&quot;font-family:宋体&quot;&gt;中的所有列级和表级约束、存储过程和触发器）。&lt;/span&gt;&lt;/p&gt;&lt;h3 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;完整性约束的具体实现&lt;/span&gt;&lt;/a&gt;&lt;/h3&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;约束定义的语法为：&lt;/span&gt;&lt;br/&gt; &lt;span style=&quot;font-family:宋体&quot;&gt;列级别（即加在字段名之后）：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;CREATE&amp;nbsp;TABLE&amp;nbsp;table_name(\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;column_name&amp;nbsp;data_type&amp;nbsp;[&amp;nbsp;[NOT&amp;nbsp;NULL]&amp;nbsp;|&amp;nbsp;[UNIQUE&amp;nbsp;[KEY]&amp;nbsp;|&amp;nbsp;PRIMARY&amp;nbsp;KEY]&amp;nbsp;|CHECK(expr)],\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&hellip;\n&amp;nbsp;)&lt;/pre&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;表级别（即加在最后）：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;CREATE&amp;nbsp;TABLE&amp;nbsp;table_name(\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;column_name&amp;nbsp;data_type,\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;column_name&amp;nbsp;data_type,\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&hellip;,\n&amp;nbsp;　　[CONSTRAINT&amp;nbsp;constraint_name]&amp;nbsp;PRIMARY&amp;nbsp;KEY&amp;nbsp;(col_name,...)\n&amp;nbsp;　　|[CONSTRAINT&amp;nbsp;constraint_name]&amp;nbsp;unique&amp;nbsp;(col_name,...)\n&amp;nbsp;　　|[CONSTRAINT&amp;nbsp;constraint_name]&amp;nbsp;foreign&amp;nbsp;KEY&amp;nbsp;(col_name)&amp;nbsp;REFERENCES&amp;nbsp;tbl_name&amp;nbsp;(index_col_name)\n&amp;nbsp;　　|check(expr)\n&amp;nbsp;)&lt;/pre&gt;&lt;h4 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;NOT NULL&lt;/a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;约束&lt;/span&gt;&lt;/h4&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;NOT NULL &lt;span style=&quot;font-family:宋体&quot;&gt;约束强制字段始终包含值。这意味着，如果不向字段添加值，就无法插入新记录或者更新记录。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;例如：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;mysql&amp;gt;&amp;nbsp;create&amp;nbsp;table&amp;nbsp;t_notnull(\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;name&amp;nbsp;varchar(20)&amp;nbsp;not&amp;nbsp;null,\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;phone&amp;nbsp;varchar(20)&amp;nbsp;not&amp;nbsp;null);\n&amp;nbsp;Query&amp;nbsp;OK,&amp;nbsp;0&amp;nbsp;rows&amp;nbsp;affected&amp;nbsp;(0.02&amp;nbsp;sec)\n&amp;nbsp;\n&amp;nbsp;mysql&amp;gt;&amp;nbsp;describe&amp;nbsp;t_notnull;\n&amp;nbsp;+-------+-------------+------+-----+---------+-------+\n&amp;nbsp;|&amp;nbsp;Field&amp;nbsp;|&amp;nbsp;Type&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;Null&amp;nbsp;|&amp;nbsp;Key&amp;nbsp;|&amp;nbsp;Default&amp;nbsp;|&amp;nbsp;Extra&amp;nbsp;|\n&amp;nbsp;+-------+-------------+------+-----+---------+-------+\n&amp;nbsp;|&amp;nbsp;name&amp;nbsp;&amp;nbsp;|&amp;nbsp;varchar(20)&amp;nbsp;|&amp;nbsp;NO&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;NULL&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|\n&amp;nbsp;|&amp;nbsp;phone&amp;nbsp;|&amp;nbsp;varchar(20)&amp;nbsp;|&amp;nbsp;NO&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;NULL&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|\n&amp;nbsp;+-------+-------------+------+-----+---------+-------+\n&amp;nbsp;2&amp;nbsp;rows&amp;nbsp;in&amp;nbsp;set&amp;nbsp;(0.03&amp;nbsp;sec)&lt;/pre&gt;&lt;h4 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;UNIQUE&lt;/a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;约束&lt;/span&gt;&lt;/h4&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;UNIQUE&lt;span style=&quot;font-family:宋体&quot;&gt;约束用于指明某一列或多个列的组合上取值必须唯一。定义了&lt;/span&gt;UNIQUE&lt;span style=&quot;font-family:宋体&quot;&gt;约束的那些列值称为唯一键，系统自动为唯一键建立唯一索引。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;定义列时添加约束代码如下：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;mysql&amp;gt;&amp;nbsp;create&amp;nbsp;table&amp;nbsp;t_unique(\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;id&amp;nbsp;int(10)&amp;nbsp;unique,\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;name&amp;nbsp;varchar(20));\n&amp;nbsp;Query&amp;nbsp;OK,&amp;nbsp;0&amp;nbsp;rows&amp;nbsp;affected&amp;nbsp;(0.02&amp;nbsp;sec)\n&amp;nbsp;\n&amp;nbsp;mysql&amp;gt;&amp;nbsp;describe&amp;nbsp;t_unique;\n&amp;nbsp;+-------+-------------+------+-----+---------+-------+\n&amp;nbsp;|&amp;nbsp;Field&amp;nbsp;|&amp;nbsp;Type&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;Null&amp;nbsp;|&amp;nbsp;Key&amp;nbsp;|&amp;nbsp;Default&amp;nbsp;|&amp;nbsp;Extra&amp;nbsp;|\n&amp;nbsp;+-------+-------------+------+-----+---------+-------+\n&amp;nbsp;|&amp;nbsp;id&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;int(10)&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;YES&amp;nbsp;&amp;nbsp;|&amp;nbsp;UNI&amp;nbsp;|&amp;nbsp;NULL&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|\n&amp;nbsp;|&amp;nbsp;name&amp;nbsp;&amp;nbsp;|&amp;nbsp;varchar(20)&amp;nbsp;|&amp;nbsp;YES&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;NULL&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|\n&amp;nbsp;+-------+-------------+------+-----+---------+-------+\n&amp;nbsp;2&amp;nbsp;rows&amp;nbsp;in&amp;nbsp;set&amp;nbsp;(0.03&amp;nbsp;sec)&lt;/pre&gt;&lt;h4 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;PRIMARY KEY&lt;/a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;约束&lt;/span&gt;&lt;/h4&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;PRIMARY KEY&lt;span style=&quot;font-family:宋体&quot;&gt;约束用于定义基本表的主键，起唯一标识作用，其值不能为&lt;/span&gt;null&lt;span style=&quot;font-family:宋体&quot;&gt;，也不能重复，以此来保证实体的完整性。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size:14px;font-family:宋体&quot;&gt;定义列时定义主键实现代码如下：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;mysql&amp;gt;&amp;nbsp;create&amp;nbsp;table&amp;nbsp;t_pk(\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;id&amp;nbsp;int(10)&amp;nbsp;primary&amp;nbsp;key,\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;name&amp;nbsp;varchar(10),\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;addr&amp;nbsp;varchar(50));\n&amp;nbsp;Query&amp;nbsp;OK,&amp;nbsp;0&amp;nbsp;rows&amp;nbsp;affected&amp;nbsp;(0.02&amp;nbsp;sec)\n&amp;nbsp;\n&amp;nbsp;mysql&amp;gt;&amp;nbsp;describe&amp;nbsp;t_pk;\n&amp;nbsp;+-------+-------------+------+-----+---------+-------+\n&amp;nbsp;|&amp;nbsp;Field&amp;nbsp;|&amp;nbsp;Type&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;Null&amp;nbsp;|&amp;nbsp;Key&amp;nbsp;|&amp;nbsp;Default&amp;nbsp;|&amp;nbsp;Extra&amp;nbsp;|\n&amp;nbsp;+-------+-------------+------+-----+---------+-------+\n&amp;nbsp;|&amp;nbsp;id&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;int(10)&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;NO&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;PRI&amp;nbsp;|&amp;nbsp;NULL&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|\n&amp;nbsp;|&amp;nbsp;name&amp;nbsp;&amp;nbsp;|&amp;nbsp;varchar(10)&amp;nbsp;|&amp;nbsp;YES&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;NULL&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|\n&amp;nbsp;|&amp;nbsp;addr&amp;nbsp;&amp;nbsp;|&amp;nbsp;varchar(50)&amp;nbsp;|&amp;nbsp;YES&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;NULL&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|\n&amp;nbsp;+-------+-------------+------+-----+---------+-------+\n&amp;nbsp;3&amp;nbsp;rows&amp;nbsp;in&amp;nbsp;set&amp;nbsp;(0.03&amp;nbsp;sec)&lt;/pre&gt;&lt;h4 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;FOREIGN KEY&lt;/a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;约束&lt;/span&gt;&lt;/h4&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;FOREIGN KEY&lt;span style=&quot;font-family:宋体&quot;&gt;约束则定义了一个表中的数据与另外一个表中数据的联系。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;一个表中的&lt;/span&gt; FOREIGN KEY &lt;span style=&quot;font-family:宋体&quot;&gt;指向另一个表中的&lt;/span&gt; PRIMARY KEY&lt;span style=&quot;font-family:宋体&quot;&gt;。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;定义列时创建外键代码如下：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;mysql&amp;gt;&amp;nbsp;create&amp;nbsp;table&amp;nbsp;t_fk(\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;f_id&amp;nbsp;int(10),\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;title&amp;nbsp;varchar(20),\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;foreign&amp;nbsp;key&amp;nbsp;(f_id)&amp;nbsp;references&amp;nbsp;t_pk(id));\n&amp;nbsp;Query&amp;nbsp;OK,&amp;nbsp;0&amp;nbsp;rows&amp;nbsp;affected&amp;nbsp;(0.02&amp;nbsp;sec)\n&amp;nbsp;\n&amp;nbsp;mysql&amp;gt;&amp;nbsp;describe&amp;nbsp;t_fk;\n&amp;nbsp;+-------+-------------+------+-----+---------+-------+\n&amp;nbsp;|&amp;nbsp;Field&amp;nbsp;|&amp;nbsp;Type&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;Null&amp;nbsp;|&amp;nbsp;Key&amp;nbsp;|&amp;nbsp;Default&amp;nbsp;|&amp;nbsp;Extra&amp;nbsp;|\n&amp;nbsp;+-------+-------------+------+-----+---------+-------+\n&amp;nbsp;|&amp;nbsp;f_id&amp;nbsp;&amp;nbsp;|&amp;nbsp;int(10)&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;YES&amp;nbsp;&amp;nbsp;|&amp;nbsp;MUL&amp;nbsp;|&amp;nbsp;NULL&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|\n&amp;nbsp;|&amp;nbsp;title&amp;nbsp;|&amp;nbsp;varchar(20)&amp;nbsp;|&amp;nbsp;YES&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;NULL&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|\n&amp;nbsp;+-------+-------------+------+-----+---------+-------+\n&amp;nbsp;2&amp;nbsp;rows&amp;nbsp;in&amp;nbsp;set&amp;nbsp;(0.03&amp;nbsp;sec)&lt;/pre&gt;&lt;h4 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;CHECK(&lt;/a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;校验&lt;/span&gt;)&lt;span style=&quot;font-family:宋体&quot;&gt;约束&lt;/span&gt;&lt;/h4&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;CHECK&lt;span style=&quot;font-family:宋体&quot;&gt;约束用来检查字段值所允许的范围。如一个字段只能输入整数，而且限定在&lt;/span&gt;0-100&lt;span style=&quot;font-family:宋体&quot;&gt;的整数，以此来保证域的完整性。&lt;/span&gt;check&lt;span style=&quot;font-family:宋体&quot;&gt;约束可以被定义为表约束，列约束、域约束或者被定义在断言中。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;每个&lt;/span&gt;check&lt;span style=&quot;font-family:宋体&quot;&gt;约束由关键字&lt;/span&gt;check&lt;span style=&quot;font-family:宋体&quot;&gt;后跟搜索条件组成，但是&lt;/span&gt;MySQL&lt;span style=&quot;font-family:宋体&quot;&gt;只是&lt;/span&gt;check&lt;span style=&quot;font-family:宋体&quot;&gt;，但是不强制&lt;/span&gt;check&lt;span style=&quot;font-family:宋体&quot;&gt;。可以采用枚举类型或集合类型来限定取值，或者设置相应的插入触发器来进行限定。&lt;/span&gt;&lt;/p&gt;&lt;h4 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;DEFAULT &lt;/a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;约束&lt;/span&gt;&lt;/h4&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;DEFAULT &lt;span style=&quot;font-family:宋体&quot;&gt;约束用于向列中插入默认值。如果没有规定其他的值，那么会将默认值添加到所有的新记录。&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;mysql&amp;gt;&amp;nbsp;create&amp;nbsp;table&amp;nbsp;t_default(\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;id&amp;nbsp;int(10),\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;name&amp;nbsp;varchar(20),\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;sex&amp;nbsp;varchar(1)&amp;nbsp;default&amp;nbsp;&amp;#39;m&amp;#39;);\nQuery&amp;nbsp;OK,&amp;nbsp;0&amp;nbsp;rows&amp;nbsp;affected&amp;nbsp;(0.02&amp;nbsp;sec)\n\nmysql&amp;gt;&amp;nbsp;describe&amp;nbsp;t_default;\n+-------+-------------+------+-----+---------+-------+\n|&amp;nbsp;Field&amp;nbsp;|&amp;nbsp;Type&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;Null&amp;nbsp;|&amp;nbsp;Key&amp;nbsp;|&amp;nbsp;Default&amp;nbsp;|&amp;nbsp;Extra&amp;nbsp;|\n+-------+-------------+------+-----+---------+-------+\n|&amp;nbsp;id&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;int(10)&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;YES&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;NULL&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|\n|&amp;nbsp;name&amp;nbsp;&amp;nbsp;|&amp;nbsp;varchar(20)&amp;nbsp;|&amp;nbsp;YES&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;NULL&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|\n|&amp;nbsp;sex&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;varchar(1)&amp;nbsp;&amp;nbsp;|&amp;nbsp;YES&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;m&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|\n+-------+-------------+------+-----+---------+-------+\n3&amp;nbsp;rows&amp;nbsp;in&amp;nbsp;set&amp;nbsp;(0.05&amp;nbsp;sec)&lt;/pre&gt;&lt;h4 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;自增&lt;/span&gt;&lt;/a&gt;&lt;/h4&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;通过对主键设置自增，可以在每次插入数据时，自动让&lt;/span&gt;id&lt;span style=&quot;font-family:宋体&quot;&gt;列增加。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;例如：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;mysql&amp;gt;&amp;nbsp;create&amp;nbsp;table&amp;nbsp;t_auto(\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;id&amp;nbsp;int(10)&amp;nbsp;auto_increment&amp;nbsp;primary&amp;nbsp;key,\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;name&amp;nbsp;varchar(20));\n&amp;nbsp;Query&amp;nbsp;OK,&amp;nbsp;0&amp;nbsp;rows&amp;nbsp;affected&amp;nbsp;(0.02&amp;nbsp;sec)\n&amp;nbsp;\n&amp;nbsp;mysql&amp;gt;&amp;nbsp;describe&amp;nbsp;t_auto;\n&amp;nbsp;+-------+-------------+------+-----+---------+----------------+\n&amp;nbsp;|&amp;nbsp;Field&amp;nbsp;|&amp;nbsp;Type&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;Null&amp;nbsp;|&amp;nbsp;Key&amp;nbsp;|&amp;nbsp;Default&amp;nbsp;|&amp;nbsp;Extra&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|\n&amp;nbsp;+-------+-------------+------+-----+---------+----------------+\n&amp;nbsp;|&amp;nbsp;id&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;int(10)&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;NO&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;PRI&amp;nbsp;|&amp;nbsp;NULL&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;auto_increment&amp;nbsp;|\n&amp;nbsp;|&amp;nbsp;name&amp;nbsp;&amp;nbsp;|&amp;nbsp;varchar(20)&amp;nbsp;|&amp;nbsp;YES&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;NULL&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|\n&amp;nbsp;+-------+-------------+------+-----+---------+----------------+\n&amp;nbsp;2&amp;nbsp;rows&amp;nbsp;in&amp;nbsp;set&amp;nbsp;(0.03&amp;nbsp;sec)&lt;/pre&gt;&lt;h3 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;CONSTRAINT&lt;/a&gt;&lt;/h3&gt;&lt;p&gt;&lt;span style=&quot;font-size:14px;font-family:宋体&quot;&gt;约束可以在创建表时规定，通过&lt;/span&gt;&lt;span style=&quot;font-size:14px;font-family:&amp;#39;Arial&amp;#39;,&amp;#39;sans-serif&amp;#39;&quot;&gt; CREATE TABLE &lt;/span&gt;&lt;span style=&quot;font-size:14px;font-family:宋体&quot;&gt;语句加在列后面，或在最后统一加约束&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;mysql&amp;gt;&amp;nbsp;create&amp;nbsp;table&amp;nbsp;t_cons(\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;id&amp;nbsp;int(10)&amp;nbsp;auto_increment,\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;mail&amp;nbsp;varchar(50),\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;constraint&amp;nbsp;pk&amp;nbsp;primary&amp;nbsp;key(id),\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;constraint&amp;nbsp;mail_un&amp;nbsp;unique(mail));\n&amp;nbsp;Query&amp;nbsp;OK,&amp;nbsp;0&amp;nbsp;rows&amp;nbsp;affected&amp;nbsp;(0.02&amp;nbsp;sec)\n&amp;nbsp;\n&amp;nbsp;mysql&amp;gt;&amp;nbsp;describe&amp;nbsp;t_cons;\n&amp;nbsp;+-------+-------------+------+-----+---------+----------------+\n&amp;nbsp;|&amp;nbsp;Field&amp;nbsp;|&amp;nbsp;Type&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;Null&amp;nbsp;|&amp;nbsp;Key&amp;nbsp;|&amp;nbsp;Default&amp;nbsp;|&amp;nbsp;Extra&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|\n&amp;nbsp;+-------+-------------+------+-----+---------+----------------+\n&amp;nbsp;|&amp;nbsp;id&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;int(10)&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;NO&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;PRI&amp;nbsp;|&amp;nbsp;NULL&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;auto_increment&amp;nbsp;|\n&amp;nbsp;|&amp;nbsp;mail&amp;nbsp;&amp;nbsp;|&amp;nbsp;varchar(50)&amp;nbsp;|&amp;nbsp;YES&amp;nbsp;&amp;nbsp;|&amp;nbsp;UNI&amp;nbsp;|&amp;nbsp;NULL&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|\n&amp;nbsp;+-------+-------------+------+-----+---------+----------------+\n&amp;nbsp;2&amp;nbsp;rows&amp;nbsp;in&amp;nbsp;set&amp;nbsp;(0.03&amp;nbsp;sec)&lt;/pre&gt;&lt;h3 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;修改约束&lt;/span&gt;&lt;/a&gt;&lt;/h3&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;约束也可以在创建表完成之后，通过&lt;/span&gt;ALTER TABLE&lt;span style=&quot;font-family:宋体&quot;&gt;指令修改约束。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;例如修改表指定主键：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;mysql&amp;gt;&amp;nbsp;alter&amp;nbsp;table&amp;nbsp;t_default&amp;nbsp;add&amp;nbsp;constraint&amp;nbsp;pk&amp;nbsp;primary&amp;nbsp;key(id);\n&amp;nbsp;Query&amp;nbsp;OK,&amp;nbsp;0&amp;nbsp;rows&amp;nbsp;affected&amp;nbsp;(0.03&amp;nbsp;sec)\n&amp;nbsp;Records:&amp;nbsp;0&amp;nbsp;&amp;nbsp;Duplicates:&amp;nbsp;0&amp;nbsp;&amp;nbsp;Warnings:&amp;nbsp;0\n&amp;nbsp;\n&amp;nbsp;mysql&amp;gt;&amp;nbsp;describe&amp;nbsp;t_default;\n&amp;nbsp;+-------+-------------+------+-----+---------+-------+\n&amp;nbsp;|&amp;nbsp;Field&amp;nbsp;|&amp;nbsp;Type&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;Null&amp;nbsp;|&amp;nbsp;Key&amp;nbsp;|&amp;nbsp;Default&amp;nbsp;|&amp;nbsp;Extra&amp;nbsp;|\n&amp;nbsp;+-------+-------------+------+-----+---------+-------+\n&amp;nbsp;|&amp;nbsp;id&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;int(10)&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;NO&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;PRI&amp;nbsp;|&amp;nbsp;0&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|\n&amp;nbsp;|&amp;nbsp;name&amp;nbsp;&amp;nbsp;|&amp;nbsp;varchar(20)&amp;nbsp;|&amp;nbsp;YES&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;NULL&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|\n&a