INSERT INTO `pa_topic` VALUES ('123', '40', '1', 'F1TuynvmghDk7C2GSI9tqY0BNwdprK', '180.111.167.141', '多表连接查询', '1555924037', '1555924037', '&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;多表连接查询举例：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;雇员表：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;mysql&amp;gt;&amp;nbsp;create&amp;nbsp;table&amp;nbsp;t_e(\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;name&amp;nbsp;varchar(20),\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;d_id&amp;nbsp;int(10)\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;)default&amp;nbsp;charset&amp;nbsp;=&amp;nbsp;utf8;\n&amp;nbsp;Query&amp;nbsp;OK,&amp;nbsp;0&amp;nbsp;rows&amp;nbsp;affected&amp;nbsp;(0.02&amp;nbsp;sec)\n&amp;nbsp;\n&amp;nbsp;mysql&amp;gt;&amp;nbsp;insert&amp;nbsp;into&amp;nbsp;t_e\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;values(&amp;#39;张三&amp;#39;,&amp;nbsp;31),\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;(&amp;#39;李四&amp;#39;,&amp;nbsp;33),\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;(&amp;#39;王五&amp;#39;,&amp;nbsp;33),\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;(&amp;#39;赵六&amp;#39;,&amp;nbsp;34),\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;(&amp;#39;小七&amp;#39;,&amp;nbsp;34),\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;(&amp;#39;八爪鱼&amp;#39;,&amp;nbsp;null);\n&amp;nbsp;Query&amp;nbsp;OK,&amp;nbsp;6&amp;nbsp;rows&amp;nbsp;affected&amp;nbsp;(0.01&amp;nbsp;sec)\n&amp;nbsp;Records:&amp;nbsp;6&amp;nbsp;&amp;nbsp;Duplicates:&amp;nbsp;0&amp;nbsp;&amp;nbsp;Warnings:&amp;nbsp;0\n&amp;nbsp;\n&amp;nbsp;mysql&amp;gt;&amp;nbsp;select&amp;nbsp;*&amp;nbsp;from&amp;nbsp;t_e;\n&amp;nbsp;+-----------+------+\n&amp;nbsp;|&amp;nbsp;name&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;d_id&amp;nbsp;|\n&amp;nbsp;+-----------+------+\n&amp;nbsp;|&amp;nbsp;张三&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;31&amp;nbsp;|\n&amp;nbsp;|&amp;nbsp;李四&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;33&amp;nbsp;|\n&amp;nbsp;|&amp;nbsp;王五&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;33&amp;nbsp;|\n&amp;nbsp;|&amp;nbsp;赵六&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;34&amp;nbsp;|\n&amp;nbsp;|&amp;nbsp;小七&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;34&amp;nbsp;|\n&amp;nbsp;|&amp;nbsp;八爪鱼&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;NULL&amp;nbsp;|\n&amp;nbsp;+-----------+------+\n&amp;nbsp;6&amp;nbsp;rows&amp;nbsp;in&amp;nbsp;set&amp;nbsp;(0.00&amp;nbsp;sec)&lt;/pre&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;部门表：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;mysql&amp;gt;&amp;nbsp;create&amp;nbsp;table&amp;nbsp;t_d(\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;d_id&amp;nbsp;int(10),\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;dname&amp;nbsp;varchar(10)\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;)default&amp;nbsp;charset&amp;nbsp;=&amp;nbsp;utf8;\n&amp;nbsp;Query&amp;nbsp;OK,&amp;nbsp;0&amp;nbsp;rows&amp;nbsp;affected&amp;nbsp;(0.01&amp;nbsp;sec)\n&amp;nbsp;\n&amp;nbsp;mysql&amp;gt;&amp;nbsp;insert&amp;nbsp;into&amp;nbsp;t_d\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;values(31,&amp;nbsp;&amp;#39;销售部&amp;#39;),\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;(33,&amp;nbsp;&amp;#39;市场部&amp;#39;),\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;(34,&amp;nbsp;&amp;#39;秘书&amp;#39;),\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;(35,&amp;nbsp;&amp;#39;工程部&amp;#39;)\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;;\n&amp;nbsp;Query&amp;nbsp;OK,&amp;nbsp;4&amp;nbsp;rows&amp;nbsp;affected&amp;nbsp;(0.02&amp;nbsp;sec)\n&amp;nbsp;Records:&amp;nbsp;4&amp;nbsp;&amp;nbsp;Duplicates:&amp;nbsp;0&amp;nbsp;&amp;nbsp;Warnings:&amp;nbsp;0\n&amp;nbsp;\n&amp;nbsp;mysql&amp;gt;&amp;nbsp;select&amp;nbsp;*&amp;nbsp;from&amp;nbsp;t_d;\n&amp;nbsp;+------+-----------+\n&amp;nbsp;|&amp;nbsp;d_id&amp;nbsp;|&amp;nbsp;dname&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|\n&amp;nbsp;+------+-----------+\n&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;31&amp;nbsp;|&amp;nbsp;销售部&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|\n&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;33&amp;nbsp;|&amp;nbsp;市场部&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|\n&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;34&amp;nbsp;|&amp;nbsp;秘书&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|\n&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;35&amp;nbsp;|&amp;nbsp;工程部&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|\n&amp;nbsp;+------+-----------+\n&amp;nbsp;4&amp;nbsp;rows&amp;nbsp;in&amp;nbsp;set&amp;nbsp;(0.00&amp;nbsp;sec)&lt;/pre&gt;&lt;h3 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;内连接&lt;/span&gt;&lt;/a&gt;&lt;/h3&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;内连接（&lt;/span&gt;inner join&lt;span style=&quot;font-family:宋体&quot;&gt;）是应用程序中用的普遍的&lt;/span&gt;&amp;quot;&lt;span style=&quot;font-family:宋体&quot;&gt;连接&lt;/span&gt;&amp;quot;&lt;span style=&quot;font-family:宋体&quot;&gt;操作，它一般都是默认连接类型。内连接基于连接谓词将两张表（如&lt;/span&gt; A &lt;span style=&quot;font-family:宋体&quot;&gt;和&lt;/span&gt; B&lt;span style=&quot;font-family:宋体&quot;&gt;）的列组合在一起，产生新的结果表。查询会将&lt;/span&gt; A &lt;span style=&quot;font-family:宋体&quot;&gt;表的每一行和&lt;/span&gt; B &lt;span style=&quot;font-family:宋体&quot;&gt;表的每一行进行比较，并找出满足连接谓词的组合。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;显式的内连接实例：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;SELECT&amp;nbsp;*&amp;nbsp;FROM&amp;nbsp;table1\n&amp;nbsp;INNER&amp;nbsp;JOIN&amp;nbsp;table2\n&amp;nbsp;ON&amp;nbsp;\n&amp;nbsp;table1.key&amp;nbsp;=&amp;nbsp;table2.key&lt;/pre&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;等价于：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;SELECT&amp;nbsp;*&amp;nbsp;FROM&amp;nbsp;table1,&amp;nbsp;table2\n&amp;nbsp;WHERE&amp;nbsp;table1.key&amp;nbsp;=&amp;nbsp;table2.key&lt;/pre&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;显式的内连接的输出结果：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;mysql&amp;gt;&amp;nbsp;select&amp;nbsp;*&amp;nbsp;from&amp;nbsp;t_e\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;inner&amp;nbsp;join&amp;nbsp;t_d\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;on\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;t_e.d_id&amp;nbsp;=&amp;nbsp;t_d.d_id;\n&amp;nbsp;+--------+------+------+-----------+\n&amp;nbsp;|&amp;nbsp;name&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;d_id&amp;nbsp;|&amp;nbsp;d_id&amp;nbsp;|&amp;nbsp;dname&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|\n&amp;nbsp;+--------+------+------+-----------+\n&amp;nbsp;|&amp;nbsp;张三&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;31&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;31&amp;nbsp;|&amp;nbsp;销售部&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|\n&amp;nbsp;|&amp;nbsp;李四&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;33&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;33&amp;nbsp;|&amp;nbsp;市场部&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|\n&amp;nbsp;|&amp;nbsp;王五&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;33&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;33&amp;nbsp;|&amp;nbsp;市场部&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|\n&amp;nbsp;|&amp;nbsp;赵六&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;34&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;34&amp;nbsp;|&amp;nbsp;秘书&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|\n&amp;nbsp;|&amp;nbsp;小七&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;34&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;34&amp;nbsp;|&amp;nbsp;秘书&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|\n&amp;nbsp;+--------+------+------+-----------+\n&amp;nbsp;5&amp;nbsp;rows&amp;nbsp;in&amp;nbsp;set&amp;nbsp;(0.00&amp;nbsp;sec)&lt;/pre&gt;&lt;h3 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;相等连接&lt;/span&gt;&lt;/a&gt;&lt;/h3&gt;&lt;p&gt;&lt;span style=&quot;font-size:14px;font-family:宋体&quot;&gt;相等连接&lt;/span&gt;&lt;span style=&quot;font-size:14px;font-family:&amp;#39;Arial&amp;#39;,&amp;#39;sans-serif&amp;#39;&quot;&gt; (equi-join&lt;/span&gt;&lt;span style=&quot;font-size:14px;font-family:宋体&quot;&gt;，或&lt;/span&gt;&lt;span style=&quot;font-size:14px;font-family:&amp;#39;Arial&amp;#39;,&amp;#39;sans-serif&amp;#39;&quot;&gt;equijoin)&lt;/span&gt;&lt;span style=&quot;font-size:14px;font-family:宋体&quot;&gt;，是比较连接的一种特例，它的连接谓词只用了相等比较。使用其他比较操作符&lt;/span&gt;&lt;span style=&quot;font-size:14px;font-family:&amp;#39;Arial&amp;#39;,&amp;#39;sans-serif&amp;#39;&quot;&gt;(&lt;/span&gt;&lt;span style=&quot;font-size:14px;font-family:宋体&quot;&gt;如&lt;/span&gt;&lt;span style=&quot;font-size:14px;font-family:&amp;#39;Arial&amp;#39;,&amp;#39;sans-serif&amp;#39;&quot;&gt;&amp;lt;&lt;/span&gt;&lt;span style=&quot;font-size:14px;font-family:&amp;#39;Arial&amp;#39;,&amp;#39;sans-serif&amp;#39;&quot;&gt;)&lt;/span&gt;&lt;span style=&quot;font-size:14px;font-family:宋体&quot;&gt;的不是相等连接。前面的查询已经展示了一个相等连接的实例：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;mysql&amp;gt;&amp;nbsp;select&amp;nbsp;*&amp;nbsp;from&amp;nbsp;t_e\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;inner&amp;nbsp;join&amp;nbsp;t_d\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;on\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;t_e.d_id&amp;nbsp;=&amp;nbsp;t_d.d_id;&lt;/pre&gt;&lt;h3 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;自然连接&lt;/span&gt;&lt;/a&gt;&lt;/h3&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;自然连接比相等连接的进一步特例化。两表做自然连接时，两表中的所有名称相同的列都将被比较，这是隐式的。自然连接得到的结果表中，两表中名称相同的列只出现一次。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;上面用于内连接的查询实例可以用自然连接的方式表示如下：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;SELECT&amp;nbsp;*&amp;nbsp;FROM&amp;nbsp;table1\n&amp;nbsp;NATURAL&amp;nbsp;JOIN&amp;nbsp;table2&lt;/pre&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;用了&lt;/span&gt;USING&lt;span style=&quot;font-family:宋体&quot;&gt;语句后，在连接表中，&lt;/span&gt;d_id&lt;span style=&quot;font-family:宋体&quot;&gt;列只出现一次，且没有表名作前缀：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;mysql&amp;gt;&amp;nbsp;select&amp;nbsp;*&amp;nbsp;from&amp;nbsp;t_e\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;natural&amp;nbsp;join&amp;nbsp;t_d;\n&amp;nbsp;+------+--------+-----------+\n&amp;nbsp;|&amp;nbsp;d_id&amp;nbsp;|&amp;nbsp;name&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;dname&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;|\n&amp;nbsp;+------+--------+-----------+\n&amp;nbsp;|&amp;nbsp;&amp;nbsp;&amp;nbsp;31&amp;nbsp;|&amp;nbsp;张三&amp;nbsp;&amp;nbsp;&amp;nbsp;|&amp;nbsp;销售部&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp