INSERT INTO `pa_topic` VALUES ('33', '36', '4', '4iD1bJm7rE3ueaHTsYgnBvVUPR6Zjx', '121.229.180.134', '【4】配置applicationContext-dao.xml', '1553657962', '1553657962', '&lt;p&gt;本节操作完成了对spring父容器中dao层扫描加载及其分页的配置。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;1、在resources中创建MyBatis-Config.xml&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;具体操作如下图所示：&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190327/1553657908552802.gif&quot; title=&quot;1553657908552802.gif&quot; alt=&quot;1553657908552802.gif&quot; width=&quot;800&quot; height=&quot;532&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;2、修改MyBatis-Config.xml&lt;/strong&gt;&lt;/p&gt;&lt;pre class=&quot;brush:xml;toolbar:false&quot;&gt;&amp;lt;?xml&amp;nbsp;version=&amp;quot;1.0&amp;quot;&amp;nbsp;encoding=&amp;quot;UTF-8&amp;quot;&amp;nbsp;?&amp;gt;\n&amp;lt;!DOCTYPE&amp;nbsp;configuration\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;PUBLIC&amp;nbsp;&amp;quot;-//mybatis.org//DTD&amp;nbsp;Config&amp;nbsp;3.0//EN&amp;quot;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;quot;http://mybatis.org/dtd/mybatis-3-config.dtd&amp;quot;&amp;gt;\n&amp;lt;configuration&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;plugins&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;plugin&amp;nbsp;interceptor=&amp;quot;com.github.pagehelper.PageInterceptor&amp;quot;&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/plugin&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/plugins&amp;gt;\n&amp;lt;/configuration&amp;gt;&lt;/pre&gt;&lt;p&gt;&lt;strong&gt;&amp;nbsp;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;3、创建applicationContext-dao.xmll&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;具体操作如下图所示：&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190327/1553657923541877.gif&quot; title=&quot;1553657923541877.gif&quot; alt=&quot;1553657923541877.gif&quot; width=&quot;800&quot; height=&quot;532&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;4、修改applicationContext-dao.xml&lt;/strong&gt;&lt;/p&gt;&lt;pre class=&quot;brush:xml;toolbar:false&quot;&gt;&amp;lt;?xml&amp;nbsp;version=&amp;quot;1.0&amp;quot;&amp;nbsp;encoding=&amp;quot;UTF-8&amp;quot;?&amp;gt;\n&amp;lt;beans&amp;nbsp;xmlns=&amp;quot;http://www.springframework.org/schema/beans&amp;quot;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;xmlns:xsi=&amp;quot;http://www.w3.org/2001/XMLSchema-instance&amp;quot;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;xsi:schemaLocation=&amp;quot;http://www.springframework.org/schema/beans&amp;nbsp;http://www.springframework.org/schema/beans/spring-beans.xsd&amp;quot;&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;!--配置数据库连接池，使用的是druid--&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;bean&amp;nbsp;id=&amp;quot;dataSource&amp;quot;&amp;nbsp;class=&amp;quot;com.alibaba.druid.pool.DruidDataSource&amp;quot;&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;property&amp;nbsp;name=&amp;quot;driverClassName&amp;quot;&amp;nbsp;value=&amp;quot;com.mysql.jdbc.Driver&amp;quot;&amp;gt;&amp;lt;/property&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;property&amp;nbsp;name=&amp;quot;url&amp;quot;&amp;nbsp;value=&amp;quot;jdbc:mysql://localhost:3306/student&amp;quot;&amp;gt;&amp;lt;/property&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;property&amp;nbsp;name=&amp;quot;username&amp;quot;&amp;nbsp;value=&amp;quot;root&amp;quot;&amp;gt;&amp;lt;/property&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;property&amp;nbsp;name=&amp;quot;password&amp;quot;&amp;nbsp;value=&amp;quot;zhangli&amp;quot;&amp;gt;&amp;lt;/property&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/bean&amp;gt;\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;!--配置SqlSession的工厂--&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;bean&amp;nbsp;id=&amp;quot;sqlSessionFactory&amp;quot;&amp;nbsp;class=&amp;quot;org.mybatis.spring.SqlSessionFactoryBean&amp;quot;&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;property&amp;nbsp;name=&amp;quot;configLocation&amp;quot;&amp;nbsp;&amp;nbsp;value=&amp;quot;classpath:Mybatis-Config.xml&amp;quot;/&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;property&amp;nbsp;name=&amp;quot;dataSource&amp;quot;&amp;nbsp;ref=&amp;quot;dataSource&amp;quot;&amp;gt;&amp;lt;/property&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/bean&amp;gt;\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;!--配置数据库操作的接口以及xml所在package--&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;bean&amp;nbsp;class=&amp;quot;org.mybatis.spring.mapper.MapperScannerConfigurer&amp;quot;&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;property&amp;nbsp;name=&amp;quot;basePackage&amp;quot;&amp;nbsp;value=&amp;quot;net.wanho.mapper&amp;quot;&amp;gt;&amp;lt;/property&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/bean&amp;gt;\n&amp;lt;/beans&amp;gt;&lt;/pre&gt;&lt;p&gt;至此，我们完成了spring父容器中dao层扫描加载及其分页的配置。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '6', '0', '0', '0', '0', '0', '0');
INSERT INTO `pa_topic` VALUES ('34', '36', '4', 'EMTGyK4riBpehglb2oFqJP9ftwCaxL', '121.229.180.134', '【5】配置applicationContext-trans.xml', '1553658499', '1553658499', '&lt;p&gt;本节操作完成了对spring父容器中事务层扫描加载的配置。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;1、在resources中创建applicationContext-trans.xml&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;具体操作如下图所示：&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190327/1553658230474310.gif&quot; title=&quot;1553658230474310.gif&quot; alt=&quot;1553658230474310.gif&quot; width=&quot;800&quot; height=&quot;532&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;2、修改applicationContext-trans.xml&lt;/strong&gt;&lt;/p&gt;&lt;pre class=&quot;brush:xml;toolbar:false&quot;&gt;&amp;lt;?xml&amp;nbsp;version=&amp;quot;1.0&amp;quot;&amp;nbsp;encoding=&amp;quot;UTF-8&amp;quot;?&amp;gt;\n&amp;lt;beans&amp;nbsp;xmlns=&amp;quot;http://www.springframework.org/schema/beans&amp;quot;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;xmlns:xsi=&amp;quot;http://www.w3.org/2001/XMLSchema-instance&amp;quot;&amp;nbsp;xmlns:aop=&amp;quot;http://www.springframework.org/schema/aop&amp;quot;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;xmlns:tx=&amp;quot;http://www.springframework.org/schema/tx&amp;quot;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;xsi:schemaLocation=&amp;quot;http://www.springframework.org/schema/beans&amp;nbsp;http://www.springframework.org/schema/beans/spring-beans.xsd&amp;nbsp;http://www.springframework.org/schema/aop&amp;nbsp;http://www.springframework.org/schema/aop/spring-aop.xsd&amp;nbsp;http://www.springframework.org/schema/tx&amp;nbsp;http://www.springframework.org/schema/tx/spring-tx.xsd&amp;quot;&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;!--设置事务管理器，引用的是applicationContext-dao.xml中配置的datasource--&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;bean&amp;nbsp;id=&amp;quot;transactionManager&amp;quot;&amp;nbsp;class=&amp;quot;org.springframework.jdbc.datasource.DataSourceTransactionManager&amp;quot;&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;property&amp;nbsp;name=&amp;quot;dataSource&amp;quot;&amp;nbsp;ref=&amp;quot;dataSource&amp;quot;&amp;gt;&amp;lt;/property&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/bean&amp;gt;\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;!--配置事务通知，引用上面设置的事务管理器\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;add、delete、update开头的方法需要事务\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;其他方法都是只读的\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;--&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;tx:advice&amp;nbsp;id=&amp;quot;advice&amp;quot;&amp;nbsp;transaction-manager=&amp;quot;transactionManager&amp;quot;&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;tx:attributes&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;tx:method&amp;nbsp;name=&amp;quot;add*&amp;quot;&amp;nbsp;propagation=&amp;quot;REQUIRED&amp;quot;/&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;tx:method&amp;nbsp;name=&amp;quot;update*&amp;quot;&amp;nbsp;propagation=&amp;quot;REQUIRED&amp;quot;/&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;tx:method&amp;nbsp;name=&amp;quot;delete*&amp;quot;&amp;nbsp;propagation=&amp;quot;REQUIRED&amp;quot;/&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;tx:method&amp;nbsp;name=&amp;quot;*&amp;quot;&amp;nbsp;read-only=&amp;quot;true&amp;quot;/&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/tx:attributes&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/tx:advice&amp;gt;\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;!--定义连接点，advisor定义了通知在连接点上执行即织入--&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;aop:config&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;aop:pointcut&amp;nbsp;id=&amp;quot;pointcut&amp;quot;&amp;nbsp;expression=&amp;quot;execution(*&amp;nbsp;net.wanho.service.*.*(..))&amp;quot;&amp;gt;&amp;lt;/aop:pointcut&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;aop:advisor&amp;nbsp;advice-ref=&amp;quot;advice&amp;quot;&amp;nbsp;pointcut-ref=&amp;quot;pointcut&amp;quot;&amp;gt;&amp;lt;/aop:advisor&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/aop:config&amp;gt;\n&amp;lt;/beans&amp;gt;&lt;/pre&gt;&lt;p&gt;至此，我们完成了对spring父容器中事务层扫描加载的配置。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '8', '0', '0', '0', '0', '0', '0');
INSERT INTO `pa_topic` VALUES ('35', '36', '4', 'ap8cqndsjzLZNMbHi10tYxkV9BAfmP', '121.229.180.134', '【6】配置web.xml', '1553669216', '1553669216', '&lt;p&gt;本节操作完成了在web.xml中对父子容器的配置。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;1、修改web.xml&lt;/strong&gt;&lt;/p&gt;&lt;pre class=&quot;brush:xml;toolbar:false&quot;&gt;&amp;lt;!DOCTYPE&amp;nbsp;web-app&amp;nbsp;PUBLIC\n&amp;nbsp;&amp;quot;-//Sun&amp;nbsp;Microsystems,&amp;nbsp;Inc.//DTD&amp;nbsp;Web&amp;nbsp;Application&amp;nbsp;2.3//EN&amp;quot;\n&amp;nbsp;&amp;quot;http://java.sun.com/dtd/web-app_2_3.dtd&amp;quot;&amp;nbsp;&amp;gt;\n&amp;nbsp;\n&amp;lt;web-app&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;lt;display-name&amp;gt;Archetype&amp;nbsp;Created&amp;nbsp;Web&amp;nbsp;Application&amp;lt;/display-name&amp;gt;\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;lt;!--该节点定义了spring父容器所要读取的文件，applicationContext-*.xml表明所有以&amp;quot;applicationContext-&amp;quot;都会被加载--&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;lt;context-param&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;param-name&amp;gt;contextConfigLocation&amp;lt;/param-name&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;param-value&amp;gt;classpath:applicationContext-*.xml&amp;lt;/param-value&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;lt;/context-param&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;lt;listener&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;listener-class&amp;gt;org.springframework.web.context.ContextLoaderListener&amp;lt;/listener-class&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;lt;/listener&amp;gt;\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;lt;!--init-param节点定义了spring子容器所要读取的文件，load-on-startup保证了系统启动之时就会加载该servlet--&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;lt;servlet&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;servlet-name&amp;gt;mvc&amp;lt;/servlet-name&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;servlet-class&amp;gt;org.springframework.web.servlet.DispatcherServlet&amp;lt;/servlet-class&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;init-param&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;param-name&amp;gt;contextConfigLocation&amp;lt;/param-name&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;param-value&amp;gt;classpath:springmvc.xml&amp;lt;/param-value&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/init-param&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;load-on-startup&amp;gt;1&amp;lt;/load-on-startup&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;lt;/servlet&amp;gt;\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;lt;!--url-pattern节点的&amp;quot;/&amp;quot;取值，保证了所有的url都会被拦截--&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;lt;servlet-mapping&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;servlet-name&amp;gt;mvc&amp;lt;/servlet-name&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;url-pattern&amp;gt;/&amp;lt;/url-pattern&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;lt;/servlet-mapping&amp;gt;\n&amp;lt;/web-app&amp;gt;&lt;/pre&gt;&lt;p&gt;父容器即spring容器，ioc、aop皆在于此；子容器即springmvc容器，mvc在这里实现。父容器先加载，子容器后加载。子容器能访问父容器，父容器不能访问子容器。&lt;/p&gt;&lt;p&gt;至此，我们完成了在web.xml中父子容器的配置。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '3', '0', '0', '0', '0', '0', '0');
INSERT INTO `pa_topic` VALUES ('36', '36', '4', 'Nipn5BrU0aW91L7yJEm6SohOP4wIqt', '121.229.180.134', '【7】完成新增学生信息api', '1553670031', '1553670031', '&lt;p&gt;本节操作将完成新增用户的api，并进行测试。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;1、前提约束&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;安装好mysql数据库&lt;/p&gt;&lt;p&gt;数据库具有student database&lt;/p&gt;&lt;p&gt;账号root，密码zhangli【请根据实际情况确定】&lt;/p&gt;&lt;p&gt;在student database中有t_student表&lt;/p&gt;&lt;p&gt;t_student表结构为 ( id int, name varchar(20))，且主键自增&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;2、创建net.wanho.entity.Student.java类，与t_stu对应，即实体类&lt;/strong&gt;&lt;/p&gt;&lt;pre class=&quot;brush:java;toolbar:false&quot;&gt;package&amp;nbsp;net.wanho.entity;\n&amp;nbsp;\nimport&amp;nbsp;java.io.Serializable;\n&amp;nbsp;\npublic&amp;nbsp;class&amp;nbsp;Student&amp;nbsp;implements&amp;nbsp;Serializable&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;private&amp;nbsp;int&amp;nbsp;id;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;private&amp;nbsp;String&amp;nbsp;name;\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;Student()&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;Student(int&amp;nbsp;id,&amp;nbsp;String&amp;nbsp;name)&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;this.id&amp;nbsp;=&amp;nbsp;id;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;this.name&amp;nbsp;=&amp;nbsp;name;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;int&amp;nbsp;getId()&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;id;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;void&amp;nbsp;setId(int&amp;nbsp;id)&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;this.id&amp;nbsp;=&amp;nbsp;id;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;String&amp;nbsp;getName()&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;name;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;void&amp;nbsp;setName(String&amp;nbsp;name)&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;this.name&amp;nbsp;=&amp;nbsp;name;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;@Override\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;String&amp;nbsp;toString()&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;&amp;quot;Student{&amp;quot;&amp;nbsp;+\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;quot;id=&amp;quot;&amp;nbsp;+&amp;nbsp;id&amp;nbsp;+\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;quot;,&amp;nbsp;name=&amp;#39;&amp;quot;&amp;nbsp;+&amp;nbsp;name&amp;nbsp;+&amp;nbsp;&amp;#39;\\&amp;#39;&amp;#39;&amp;nbsp;+\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;}&amp;#39;;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n}&lt;/pre&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;3、创建net.wanho.mapper.StudentMapper.java接口。&lt;/strong&gt;&lt;/p&gt;&lt;pre class=&quot;brush:java;toolbar:false&quot;&gt;package&amp;nbsp;net.wanho.mapper;\n&amp;nbsp;\nimport&amp;nbsp;net.wanho.entity.Student;\n&amp;nbsp;\npublic&amp;nbsp;interface&amp;nbsp;StudentMapper&amp;nbsp;{\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;void&amp;nbsp;add(Student&amp;nbsp;student)&amp;nbsp;throws&amp;nbsp;Exception;\n}&lt;/pre&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;br/&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;4、创建net/wanho/mapper/StudentMapper.xml【此文件应与上述接口同名】。&lt;/strong&gt;&lt;/p&gt;&lt;pre class=&quot;brush:java;toolbar:false&quot;&gt;&amp;lt;?xml&amp;nbsp;version=&amp;quot;1.0&amp;quot;&amp;nbsp;encoding=&amp;quot;UTF-8&amp;quot;&amp;nbsp;?&amp;gt;\n&amp;lt;!DOCTYPE&amp;nbsp;mapper\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;PUBLIC&amp;nbsp;&amp;quot;-//mybatis.org//DTD&amp;nbsp;Mapper&amp;nbsp;3.0//EN&amp;quot;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;quot;http://mybatis.org/dtd/mybatis-3-mapper.dtd&amp;quot;&amp;gt;\n&amp;lt;mapper&amp;nbsp;namespace=&amp;quot;net.wanho.mapper.StudentMapper&amp;quot;&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;!--id的值与接口中方法名同名，parameterType即方法的参数类型，\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;useGeneratedKeys即使用自增主键，keyProperty定义了主键--&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;insert&amp;nbsp;id=&amp;quot;add&amp;quot;&amp;nbsp;parameterType=&amp;quot;net.wanho.entity.Student&amp;quot;&amp;nbsp;useGeneratedKeys=&amp;quot;true&amp;quot;&amp;nbsp;keyProperty=&amp;quot;id&amp;quot;&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;insert&amp;nbsp;into&amp;nbsp;t_student(name)&amp;nbsp;values(#{name})\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/insert&amp;gt;\n&amp;lt;/mapper&amp;gt;&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;5、创建net.wanho.service.StudentServiceI.java接口&lt;/strong&gt;&lt;/p&gt;&lt;pre class=&quot;brush:java;toolbar:false&quot;&gt;package&amp;nbsp;net.wanho.service;\n&amp;nbsp;\nimport&amp;nbsp;net.wanho.entity.Student;\n&amp;nbsp;\npublic&amp;nbsp;interface&amp;nbsp;StudentServiceI&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;void&amp;nbsp;addStudent(Student&amp;nbsp;student)throws&amp;nbsp;Exception;\n}&lt;/pre&gt;&lt;p&gt;&lt;strong&gt;&lt;br/&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;6、创建net.wanho.service.impl.StudentServiceImpl.java，即上述接口的实现类&lt;/strong&gt;&lt;/p&gt;&lt;pre class=&quot;brush:java;toolbar:false&quot;&gt;package&amp;nbsp;net.wanho.service.impl;\n&amp;nbsp;\nimport&amp;nbsp;net.wanho.entity.Student;\nimport&amp;nbsp;net.wanho.mapper.StudentMapper;\nimport&amp;nbsp;net.wanho.service.StudentServiceI;\nimport&amp;nbsp;org.springframework.stereotype.Service;\n&amp;nbsp;\nimport&amp;nbsp;javax.annotation.Resource;\n&amp;nbsp;\n@Service\npublic&amp;nbsp;class&amp;nbsp;StudentServiceImpl&amp;nbsp;implements&amp;nbsp;StudentServiceI&amp;nbsp;{\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//注入StudentMapper\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;@Resource\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;private&amp;nbsp;StudentMapper&amp;nbsp;studentMapper;\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;void&amp;nbsp;addStudent(Student&amp;nbsp;student)&amp;nbsp;throws&amp;nbsp;Exception&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;studentMapper.add(student);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;7、创建net.wanho.controller.StudentController.java，即api的入口类&lt;/strong&gt;&lt;/p&gt;&lt;pre class=&quot;brush:java;toolbar:false&quot;&gt;package&amp;nbsp;net.wanho.controller;\n&amp;nbsp;\nimport&amp;nbsp;com.alibaba.fastjson.JSONObject;\nimport&amp;nbsp;net.wanho.entity.Student;\nimport&amp;nbsp;net.wanho.service.StudentServiceI;\nimport&amp;nbsp;org.springframework.stereotype.Controller;\nimport&amp;nbsp;org.springframework.web.bind.annotation.RequestMapping;\nimport&amp;nbsp;org.springframework.web.bind.annotation.RequestMethod;\nimport&amp;nbsp;org.springframework.web.bind.annotation.ResponseBody;\n&amp;nbsp;\nimport&amp;nbsp;javax.annotation.Resource;\n&amp;nbsp;\n@Controller\npublic&amp;nbsp;class&amp;nbsp;StudentController&amp;nbsp;{\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;@Resource\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;private&amp;nbsp;StudentServiceI&amp;nbsp;studentService;\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;@RequestMapping(value=&amp;quot;/student/add/{name}&amp;quot;,method&amp;nbsp;=&amp;nbsp;RequestMethod.GET)\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;@ResponseBody\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;JSONObject&amp;nbsp;addStudent(Student&amp;nbsp;student)\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;JSONObject&amp;nbsp;ret&amp;nbsp;=&amp;nbsp;new&amp;nbsp;JSONObject();\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;try&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;studentService.addStudent(student);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ret.put(&amp;quot;status&amp;quot;,200);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ret.put(&amp;quot;msg&amp;quot;,&amp;quot;add&amp;nbsp;success:&amp;quot;+student.getId());\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;catch(Exception&amp;nbsp;e)\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ret.put(&amp;quot;status&amp;quot;,100);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ret.put(&amp;quot;msg&amp;quot;,&amp;quot;add&amp;nbsp;error&amp;quot;);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;e.printStackTrace();\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;ret;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n}&lt;/pre&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;br/&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;8、测试1&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;打开浏览器输入http://localhost:8088/student/add/ali，反复执行，会看到返回的主键一直在自增。具体操作如下图所示：&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190327/1553669961940682.gif&quot; title=&quot;1553669961940682.gif&quot; alt=&quot;1553669961940682.gif&quot; width=&quot;800&quot; height=&quot;490&quot;/&gt;&lt;/p&gt;&lt;p&gt;（测试新增学生api）&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;也可以采用如下方式查看是否成功。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;9、测试2&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;打开mysql命令行，查看表中数据，具体操作如下图所示：&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190327/1553669999653482.gif&quot; title=&quot;1553669999653482.gif&quot; alt=&quot;1553669999653482.gif&quot; width=&quot;800&quot; height=&quot;381&quot;/&gt;&lt;/p&gt;&lt;p&gt;（mysql命令行查看数据是否插入）&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;至此，我们完成了新增学生的api，并且完成了测试。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '6', '0', '0', '0', '0', '0', '0');
INSERT INTO `pa_topic` VALUES ('87', '40', '1', '6wAUD8GYKxHbVguMmjeihl0Na1TERo', '180.111.167.141', 'Part 1.1、基本概念', '1554953653', '1554953653', '&lt;h3 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;数据（&lt;/span&gt;Data&lt;/a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;）&lt;/span&gt;&lt;/h3&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;描述事物的符号称为&lt;/span&gt;&ldquo;&lt;span style=&quot;font-family:宋体&quot;&gt;数据&lt;/span&gt;&rdquo;&lt;span style=&quot;font-family:宋体&quot;&gt;，数据是数据库中存储的基本对象。描写事物的符号有多种形式，可以是数字、文字、图形、图像和声音，但它们都是经过数字化后存入计算机的。&lt;/span&gt;&lt;/p&gt;&lt;h3 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;数据库（&lt;/span&gt;Data Base&lt;/a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;）&lt;/span&gt;&lt;/h3&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;是一个组织内被应用程序使用的逻辑相一致的相关数据的集合。&lt;/span&gt;&lt;/p&gt;&lt;h3 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;数据库的优点&lt;/span&gt;&lt;/a&gt;&lt;/h3&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font:9px &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;冗余较少&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font:9px &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;避免不一致性&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font:9px &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;效率&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font:9px &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;数据完整性&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font:9px &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;机密性&lt;/span&gt;&lt;/p&gt;&lt;h3 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;数据库管理系统（&lt;/span&gt;DBMS&lt;/a&gt;&lt;span style=&quot;font-family: 宋体&quot;&gt;）&lt;/span&gt;&lt;/h3&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;数据库管理系统是供用户使用，在操作系统之上的一层数据管理软件。包含功能：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font:9px &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;数据定义功能&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font:9px &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;数据操纵功能&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font:9px &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;数据库的运行管理&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font:9px &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;数据库的建立和维护功能&lt;/span&gt;&lt;/p&gt;&lt;h3 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;数据库系统（&lt;/span&gt;DBS&lt;/a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;）&lt;/span&gt;&lt;/h3&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;数据库系统一般由数据库、数据库管理系统（及其开发工具）、应用系统、数据库管理员和用户构成。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: center;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190411/1554953576995926.png&quot; title=&quot;1554953576995926.png&quot; alt=&quot;image.png&quot;/&gt;&lt;/p&gt;&lt;h3 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;数据库模型&lt;/span&gt;&lt;/a&gt;&lt;/h3&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;数据库模型定义了数据的逻辑设计，它也描述了不同数据之间的联系。曾经出现过以下几种模型：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font:9px &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;层次模型&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font:9px &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;网状模型&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font:9px &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;关系模型（&lt;/span&gt;Relational model&lt;span style=&quot;font-family: 宋体&quot;&gt;）&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;关系模型是现今最常用的模型。&lt;/span&gt;&lt;/p&gt;&lt;h3 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;关系数据库模型&lt;/span&gt;&lt;/a&gt;&lt;/h3&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;关系型数据库中，数据是通过关系的集合来表示的。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font:9px &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;关系是一张二维表。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font:9px &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;在关系数据库中，每一种关系具有唯一的名称。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font:9px &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;关系中的每一列称为属性，通常也称为字段。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font:9px &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;关系中属性的总数称为关系的度。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font:9px &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;关系中的行称为元组，通常也称为记录。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font:9px &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;关系中元组的个数叫做关系的基数。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font:9px &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;元组中一个属性的值叫做分量。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;关系举例：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190411/1554953618502172.png&quot; title=&quot;1554953618502172.png&quot; alt=&quot;image.png&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '3', '0', '0', '0', '0', '0', '0');
INSERT INTO `pa_topic` VALUES ('88', '40', '1', 'om6uBYEr1HFncqjvltQJxy4ZWd2w7V', '180.111.167.141', 'Part 1.2、数据库设计', '1554954513', '1554954513', '&lt;h3 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;数据库设计步骤&lt;/span&gt;&lt;/a&gt;&lt;/h3&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font:9px &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;通过对需求的分析，建立实体关系模型（&lt;/span&gt;ERM&lt;span style=&quot;font-family: 宋体&quot;&gt;）。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font:9px &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;然后在关系数据库中，建立基于&lt;/span&gt;ERM&lt;span style=&quot;font-family: 宋体&quot;&gt;的关系和规范化这些关系。&lt;/span&gt;&lt;/p&gt;&lt;h3 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;实体关系模型&lt;/span&gt;&lt;/a&gt;&lt;/h3&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;通常使用实体关系（&lt;/span&gt;E-R&lt;span style=&quot;font-family:宋体&quot;&gt;）图来表示实体和实体之间的关系。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;其中：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font:9px &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;矩形表示实体集。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font:9px &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;椭圆形表示属性。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font:9px &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;菱形表示关系集。可以是&lt;/span&gt;1&lt;span style=&quot;font-family:宋体&quot;&gt;对&lt;/span&gt;1&lt;span style=&quot;font-family:宋体&quot;&gt;，&lt;/span&gt;1&lt;span style=&quot;font-family:宋体&quot;&gt;对多，多对&lt;/span&gt;1&lt;span style=&quot;font-family:宋体&quot;&gt;和多对多。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font:9px &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;线连接属性和实体以及连接实体集和关系集。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: center;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190411/1554953745217361.png&quot; title=&quot;1554953745217361.png&quot; alt=&quot;image.png&quot;/&gt;&lt;/p&gt;&lt;h3 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;从&lt;/span&gt;E-R&lt;/a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;图到关系&lt;/span&gt;&lt;/h3&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font:9px &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;对于&lt;/span&gt;E-R&lt;span style=&quot;font-family:宋体&quot;&gt;图中的每个实体集，都创建一个关系。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font:9px &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;对于&lt;/span&gt;E-R&lt;span style=&quot;font-family:宋体&quot;&gt;图中的每个关系集，都创建一个关系。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190411/1554953771187487.png&quot; title=&quot;1554953771187487.png&quot; alt=&quot;image.png&quot;/&gt;&lt;/p&gt;&lt;h3 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;规范化&lt;/span&gt;&lt;/h3&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;规范化是一个处理过程，通过此过程将给定的一组关系转化成一组具有更坚固结构的新关系。可以避免数据冗余，插入异常等问题。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;规范化过程定义了一组层次范式（&lt;/span&gt;NF&lt;span style=&quot;font-family:宋体&quot;&gt;）。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;层次首先要满足低级范式，才能满足高级范式。&lt;/span&gt;&lt;/p&gt;&lt;h4 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;第一范式（&lt;/span&gt;1NF&lt;/a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;）&lt;/span&gt;&lt;/h4&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;数据库表中的字段都是单一属性的，不可再分。这个单一属性由基本类型构成，包括整型、实数、字符型、逻辑型、日期型等。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;不符合第一范式的不能称之为关系数据库。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190411/1554953802177431.png&quot; title=&quot;1554953802177431.png&quot; alt=&quot;image.png&quot;/&gt;&lt;/p&gt;&lt;p&gt;如&lt;span style=&quot;font-family: 宋体;&quot;&gt;下的数据库表是不符合第一范式的：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190411/1554953830110017.png&quot; title=&quot;1554953830110017.png&quot; alt=&quot;image.png&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;如&lt;/span&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;下的数据库表是符合第一范式的：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;h4 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;第二范式（&lt;/span&gt;2NF&lt;/a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;）&lt;/span&gt;&lt;/h4&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;数据库表中不存在非关键字段对任一候选关键字段的部分函数依赖（部分函数依赖指的是存在组合关键字中的某些字段决定非关键字段的情况），也即所有非关键字段都完全依赖于任意一组候选关键字。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;假定选课关系表为：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;SelectCourse(学号,&amp;nbsp;姓名,&amp;nbsp;年龄,&amp;nbsp;课程名称,&amp;nbsp;成绩,&amp;nbsp;学分)，关键字为组合关键字(学号,&amp;nbsp;课程名称)。\n&amp;nbsp;由于存在如下决定关系：不符合第二范式。\n&amp;nbsp;(学号)&rarr;&amp;nbsp;(姓名,&amp;nbsp;年龄),&amp;nbsp;(课程名称)&amp;nbsp;&rarr;&amp;nbsp;(学分)&lt;/pre&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;将选课关系表&lt;/span&gt;SelectCourse&lt;span style=&quot;font-family:宋体&quot;&gt;改为如下三个表：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;学生：Student(学号,&amp;nbsp;姓名,&amp;nbsp;年龄)；\n&amp;nbsp;课程：Course(课程名称,&amp;nbsp;学分)；\n&amp;nbsp;选课关系：SelectCourse(学号,&amp;nbsp;课程名称,&amp;nbsp;成绩)&lt;/pre&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;这样的数据库表是符合第二范式的。&lt;/span&gt;&lt;/p&gt;&lt;h4 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;第三范式（&lt;/span&gt;3NF&lt;/a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;）&lt;/span&gt;&lt;/h4&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;在第二范式的基础上，数据表中如果不存在非关键字段对任一候选关键字段的传递函数依赖则符合第三范式。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;假定学生关系表为：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;Student(学号,&amp;nbsp;姓名,&amp;nbsp;年龄,&amp;nbsp;所在学院,&amp;nbsp;学院地点,&amp;nbsp;学院电话)，关键字为单一关键字&ldquo;学号&rdquo;，\n&amp;nbsp;存在如下决定关系：(学号)&amp;nbsp;&rarr;&amp;nbsp;(姓名,&amp;nbsp;年龄,&amp;nbsp;所在学院,&amp;nbsp;学院地点,&amp;nbsp;学院电话)。\n&amp;nbsp;符合2NF的，但是不符合3NF，因为存在如下决定关系：(学号)&amp;nbsp;&rarr;&amp;nbsp;(所在学院)&amp;nbsp;&rarr;&amp;nbsp;(学院地点,&amp;nbsp;学院电话)，\n&amp;nbsp;即存在非关键字段&ldquo;学院地点&rdquo;、&ldquo;学院电话&rdquo;对关键字段&ldquo;学号&rdquo;的传递函数依赖。&lt;/pre&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;将学生关系表分为如下两个表：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;学生：(学号,&amp;nbsp;姓名,&amp;nbsp;年龄,&amp;nbsp;所在学院)\n&amp;nbsp;学院：(学院,&amp;nbsp;地点,&amp;nbsp;电话)&lt;/pre&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;这样的数据库表是符合第三范式的。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2', '0', '0', '0', '0', '0', '0');
INSERT INTO `pa_topic` VALUES ('89', '40', '1', 'xMJXTWsfPyu7Gq2Ieb15gVkF6ODnaR', '180.111.167.141', 'Part 1.3、数据库的安装和登录', '1554963228', '1554963228', '&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;在这里我们使用&lt;/span&gt;MySQL&lt;span style=&quot;font-family:宋体&quot;&gt;数据库进行后面的语言举例，首先需要安装&lt;/span&gt;MySQL&lt;span style=&quot;font-family:宋体&quot;&gt;数据库。&lt;/span&gt;&lt;/p&gt;&lt;h3 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;安装&lt;/span&gt;MySQL&lt;/a&gt;&lt;/h3&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;安装&lt;/span&gt;MySQL&lt;span style=&quot;font-family:宋体&quot;&gt;可以从&lt;/span&gt;MySQL&lt;span style=&quot;font-family:宋体&quot;&gt;的官网下载适合平台的安装包：&lt;/span&gt;&lt;a href=&quot;https://dev.mysql.com/downloads/mysql/&quot;&gt;&lt;span style=&quot;font-size:16px;color:windowtext;text-underline: none&quot;&gt;https://dev.mysql.com/downloads/mysql/&lt;/span&gt;&lt;/a&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;也可以安装一些包含了&lt;/span&gt;MySQL&lt;span style=&quot;font-family:宋体&quot;&gt;的第三方软件包，例如&lt;/span&gt;AppServ&lt;span style=&quot;font-family:宋体&quot;&gt;等。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;安装过程：&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190411/1554962007845907.png&quot; title=&quot;1554962007845907.png&quot; alt=&quot;image.png&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;接下来选择接受许可协议：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190411/1554962033375094.png&quot; title=&quot;1554962033375094.png&quot; alt=&quot;image.png&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190411/1554962147463239.png&quot; title=&quot;1554962147463239.png&quot; alt=&quot;image.png&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;在安装类别中，通常选择&lt;/span&gt;Custom&lt;span style=&quot;font-family:宋体&quot;&gt;，进行自定义设置：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;选择需要调整的安装组件，或保留默认，选择&lt;/span&gt;next&lt;span style=&quot;font-family:宋体&quot;&gt;：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190411/1554962226775138.png&quot; title=&quot;1554962226775138.png&quot; alt=&quot;image.png&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;本页点击&lt;/span&gt;Install&lt;span style=&quot;font-family:宋体&quot;&gt;即可安装：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190411/1554962248761291.png&quot; title=&quot;1554962248761291.png&quot; alt=&quot;image.png&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;安装过程中弹出此界面，选择两次&lt;/span&gt;next&lt;span style=&quot;font-family:宋体&quot;&gt;：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190411/1554962266691430.png&quot; title=&quot;1554962266691430.png&quot; alt=&quot;image.png&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size:14px;font-family:宋体&quot;&gt;接下来安装完成，选中&lt;/span&gt;&lt;span style=&quot;font-size:14px;font-family:&amp;#39;Arial&amp;#39;,&amp;#39;sans-serif&amp;#39;&quot;&gt;Launch the MySQL Instance Configuration Wizard&lt;/span&gt;&lt;span style=&quot;font-size:14px;font-family:宋体&quot;&gt;：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190411/1554962332209439.png&quot; title=&quot;1554962332209439.png&quot; alt=&quot;image.png&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190411/1554962348750205.png&quot; title=&quot;1554962348750205.png&quot; alt=&quot;image.png&quot;/&gt;&lt;/p&gt;&lt;p&gt;将&lt;span style=&quot;font-family: 宋体;&quot;&gt;打开配置向导：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;选择&lt;/span&gt;Standard Configuration&lt;span style=&quot;font-family:宋体&quot;&gt;：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190411/1554962437805374.png&quot; title=&quot;1554962437805374.png&quot; alt=&quot;image.png&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190411/1554962460723848.png&quot; title=&quot;1554962460723848.png&quot; alt=&quot;image.png&quot;/&gt;&lt;/p&gt;&lt;p&gt;设&lt;span style=&quot;font-family: 宋体;&quot;&gt;置&lt;/span&gt;MySQL&lt;span style=&quot;font-family: 宋体;&quot;&gt;实例：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;设置&lt;/span&gt;root&lt;span style=&quot;font-family:宋体&quot;&gt;用户的密码和访问限制：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190411/1554962486466438.png&quot; title=&quot;1554962486466438.png&quot; alt=&quot;image.png&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190411/1554962546801319.png&quot; title=&quot;1554962546801319.png&quot; alt=&quot;image.png&quot;/&gt;&lt;/p&gt;&lt;p&gt;下一步&lt;span style=&quot;font-family: 宋体;&quot;&gt;将进行设置：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190411/1554962690699624.png&quot; title=&quot;1554962690699624.png&quot; alt=&quot;image.png&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;至此安装完成：&lt;/span&gt;&lt;/p&gt;&lt;h3 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;登录&lt;/span&gt;MySQL&lt;/a&gt;&lt;/h3&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;在命令行下使用&lt;/span&gt;mysql&lt;span style=&quot;font-family:宋体&quot;&gt;命令登录到&lt;/span&gt;MySQL&lt;span style=&quot;font-family:宋体&quot;&gt;服务器，格式为：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;mysql&amp;nbsp;-h&amp;nbsp;主机名&amp;nbsp;-u&amp;nbsp;用户名&amp;nbsp;-p&lt;/pre&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;参数说明：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font:9px &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;-h: &lt;span style=&quot;font-family:宋体&quot;&gt;指定客户端所要登录的&lt;/span&gt; MySQL &lt;span style=&quot;font-family:宋体&quot;&gt;主机名&lt;/span&gt;, &lt;span style=&quot;font-family:宋体&quot;&gt;登录本机&lt;/span&gt;(localhost &lt;span style=&quot;font-family:宋体&quot;&gt;或&lt;/span&gt; 127.0.0.1)&lt;span style=&quot;font-family:宋体&quot;&gt;该参数可以省略；&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font:9px &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;-u: &lt;span style=&quot;font-family:宋体&quot;&gt;登录的用户名；&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font:9px &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;-p: &lt;span style=&quot;font-family:宋体&quot;&gt;告诉服务器将会使用一个密码来登录&lt;/span&gt;, &lt;span style=&quot;font-family:宋体&quot;&gt;如果所要登录的用户名密码为空&lt;/span&gt;, &lt;span style=&quot;font-family:宋体&quot;&gt;可以忽略此选项。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;如果我们要登录本机的&lt;/span&gt; MySQL &lt;span style=&quot;font-family:宋体&quot;&gt;数据库，只需要输入以下命令即可：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;mysql&amp;nbsp;-u&amp;nbsp;root&amp;nbsp;-p&lt;/pre&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;按回车确认&lt;/span&gt;, &lt;span style=&quot;font-family:宋体&quot;&gt;如果安装正确且&lt;/span&gt; MySQL &lt;span style=&quot;font-family:宋体&quot;&gt;正在运行&lt;/span&gt;, &lt;span style=&quot;font-family:宋体&quot;&gt;会得到以下响应：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;Enter&amp;nbsp;password:&lt;/pre&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;若密码存在&lt;/span&gt;, &lt;span style=&quot;font-family:宋体&quot;&gt;输入密码登录&lt;/span&gt;, &lt;span style=&quot;font-family:宋体&quot;&gt;不存在则直接按回车登录。登录成功后你将会看到&lt;/span&gt; Welecome to the MySQL monitor... &lt;span style=&quot;font-family: 宋体&quot;&gt;的提示语。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;然后命令提示符会一直以&lt;/span&gt;mysq&amp;gt;&lt;span style=&quot;font-family:宋体&quot;&gt;加一个闪烁的光标等待命令的输入&lt;/span&gt;, &lt;span style=&quot;font-family:宋体&quot;&gt;输入&lt;/span&gt;exit&lt;span style=&quot;font-family:宋体&quot;&gt;或&lt;/span&gt;quit&lt;span style=&quot;font-family:宋体&quot;&gt;退出登录。&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;C:\\WINDOWS\\system32&amp;gt;mysql&amp;nbsp;-u&amp;nbsp;root&amp;nbsp;-p\n&amp;nbsp;Enter&amp;nbsp;password:&amp;nbsp;******\n&amp;nbsp;Welcome&amp;nbsp;to&amp;nbsp;the&amp;nbsp;MySQL&amp;nbsp;monitor.&amp;nbsp;&amp;nbsp;Commands&amp;nbsp;end&amp;nbsp;with&amp;nbsp;;&amp;nbsp;or&amp;nbsp;\\g.\n&amp;nbsp;Your&amp;nbsp;MySQL&amp;nbsp;connection&amp;nbsp;id&amp;nbsp;is&amp;nbsp;2\n&amp;nbsp;Server&amp;nbsp;version:&amp;nbsp;5.5.21&amp;nbsp;MySQL&amp;nbsp;Community&amp;nbsp;Server&amp;nbsp;(GPL)\n&amp;nbsp;\n&amp;nbsp;Copyright&amp;nbsp;(c)&amp;nbsp;2000,&amp;nbsp;2016,&amp;nbsp;Oracle&amp;nbsp;and/or&amp;nbsp;its&amp;nbsp;affiliates.&amp;nbsp;All&amp;nbsp;rights&amp;nbsp;reserved.\n&amp;nbsp;\n&amp;nbsp;Oracle&amp;nbsp;is&amp;nbsp;a&amp;nbsp;registered&amp;nbsp;trademark&amp;nbsp;of&amp;nbsp;Oracle&amp;nbsp;Corporation&amp;nbsp;and/or&amp;nbsp;its\n&amp;nbsp;affiliates.&amp;nbsp;Other&amp;nbsp;names&amp;nbsp;may&amp;nbsp;be&amp;nbsp;trademarks&amp;nbsp;of&amp;nbsp;their&amp;nbsp;respective\n&amp;nbsp;owners.\n&amp;nbsp;\n&amp;nbsp;Type&amp;nbsp;&amp;#39;help;&amp;#39;&amp;nbsp;or&amp;nbsp;&amp;#39;\\h&amp;#39;&amp;nbsp;for&amp;nbsp;help.&amp;nbsp;Type&amp;nbsp;&amp;#39;\\c&amp;#39;&amp;nbsp;to&amp;nbsp;clear&amp;nbsp;the&amp;nbsp;current&amp;nbsp;input&amp;nbsp;statement.\n&amp;nbsp;\n&amp;nbsp;mysql&amp;gt;&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1', '0', '0', '0', '0', '0', '0');
INSERT INTO `pa_topic` VALUES ('90', '40', '1', '2EfscFp7T8UmvWrCjIgXQ5uZHRzaqB', '180.111.167.141', 'Part 2.1、结构化查询语言（SQL）', '1554963624', '1554963624', '&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;结构化查询语言&lt;/span&gt;(Structured Query Language)&lt;span style=&quot;font-family:宋体&quot;&gt;是用于关系型数据库上的标准语言。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font-variant-numeric: normal;font-variant-east-asian: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;对于绝大部分数据库管理系统工具而言，&lt;/span&gt;SQL&lt;span style=&quot;font-family: 宋体&quot;&gt;通用性高。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font-variant-numeric: normal;font-variant-east-asian: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;SQL &lt;span style=&quot;font-family:宋体&quot;&gt;对大小写不敏感。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top:0;margin-right:0;margin-bottom:13px;margin-left:77px&quot;&gt;&lt;span style=&quot;font-family:Symbol&quot;&gt;&middot;&lt;span style=&quot;font-variant-numeric: normal;font-variant-east-asian: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: &amp;#39;Times New Roman&amp;#39;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;某些数据库系统要求在每条&lt;/span&gt; SQL &lt;span style=&quot;font-family: 宋体&quot;&gt;命令的末端使用分号。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;SQL &lt;span style=&quot;font-family:宋体&quot;&gt;分为两个部分：&lt;/span&gt;DDL&lt;span style=&quot;font-family:宋体&quot;&gt;和&lt;/span&gt;DML&lt;span style=&quot;font-family:宋体&quot;&gt;。&lt;/span&gt;&lt;/p&gt;&lt;h3 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;数据定义语言（&lt;/span&gt;DDL&lt;/a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;）：&lt;/span&gt;&lt;/h3&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;数据库，表，约束，索引，视图的定义，修改和删除。&lt;/span&gt;&lt;/p&gt;&lt;h3 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;数据操作语言（&lt;/span&gt;DML&lt;/a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;）：&lt;/span&gt;&lt;/h3&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;增，删，改，查。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;a&gt;&lt;span style=&quot;font-size:14px;font-family:宋体&quot;&gt;本课程中以&lt;/span&gt;&lt;span style=&quot;font-size:14px;font-family:&amp;#39;Arial&amp;#39;,&amp;#39;sans-serif&amp;#39;&quot;&gt;MySQL&lt;/span&gt;&lt;span style=&quot;font-size:14px;font-family:宋体&quot;&gt;中使用的&lt;/span&gt;&lt;span style=&quot;font-size: 14px;font-family:&amp;#39;Arial&amp;#39;,&amp;#39;sans-serif&amp;#39;&quot;&gt;SQL&lt;/span&gt;&lt;span style=&quot;font-size:14px;font-family:宋体&quot;&gt;为例，其它数据库管理系统的&lt;/span&gt;&lt;span style=&quot;font-size:14px;font-family:&amp;#39;Arial&amp;#39;,&amp;#39;sans-serif&amp;#39;&quot;&gt;SQL&lt;/span&gt;&lt;span style=&quot;font-size:14px;font-family:宋体&quot;&gt;有一些语法差异，但大体相同。&lt;/span&gt;&lt;/a&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;不同的数据库中，数据类型可能会各不相同，甚至一些数据库中，同样的数据类型名称代表的内容也不一样。所以要根据数据库的官方文档来确定数据类型的格式。&lt;/span&gt;&lt;/p&gt;&lt;table width=&quot;100%&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td style=&quot;border:solid #DDDDDD 1px;background:white;padding:5px 12px 5px 12px&quot;&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;数据类型&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;border:solid #DDDDDD 1px;border-left:none;background:white;padding:5px 12px 5px 12px&quot;&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;描述&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;border:solid #DDDDDD 1px;border-top:none;background:white;padding:5px 12px 5px 12px&quot;&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;integer(size)&lt;span style=&quot;font-family:宋体&quot;&gt;，&lt;/span&gt;int(size)&lt;span style=&quot;font-family:宋体&quot;&gt;，&lt;/span&gt;smallint(size)&lt;span style=&quot;font-family:宋体&quot;&gt;，&lt;/span&gt;tinyint(size)&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;border-top:none;border-left:none;border-bottom:solid #DDDDDD 1px;border-right:solid #DDDDDD 1px;background:white;padding:5px 12px 5px 12px&quot;&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;仅容纳整数。在括号内规定数字的最大位数。&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;border:solid #DDDDDD 1px;border-top:none;background:#F8F8F8;padding:5px 12px 5px 12px&quot;&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;decimal(size,d)&lt;span style=&quot;font-family:宋体&quot;&gt;，&lt;/span&gt;numeric(size,d)&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;border-top:none;border-left:none;border-bottom:solid #DDDDDD 1px;border-right:solid #DDDDDD 1px;background:#F8F8F8;padding:5px 12px 5px 12px&quot;&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;容纳带有小数的数字。&lt;/span&gt;&amp;quot;size&amp;quot; &lt;span style=&quot;font-family:宋体&quot;&gt;规定数字的最大位数。&lt;/span&gt;&amp;quot;d&amp;quot; &lt;span style=&quot;font-family:宋体&quot;&gt;规定小数点右侧的最大位数。&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;border:solid #DDDDDD 1px;border-top:none;background:white;padding:5px 12px 5px 12px&quot;&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;char(size)&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;border-top:none;border-left:none;border-bottom:solid #DDDDDD 1px;border-right:solid #DDDDDD 1px;background:white;padding:5px 12px 5px 12px&quot;&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;容纳固定长度的字符串（可容纳字母、数字以及特殊字符）。在括号中规定字符串的长度。&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;border:solid #DDDDDD 1px;border-top:none;background:#F8F8F8;padding:5px 12px 5px 12px&quot;&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;varchar(size)&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;border-top:none;border-left:none;border-bottom:solid #DDDDDD 1px;border-right:solid #DDDDDD 1px;background:#F8F8F8;padding:5px 12px 5px 12px&quot;&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;容纳可变长度的字符串（可容纳字母、数字以及特殊的字符）。在括号中规定字符串的最大长度。&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;border:solid #DDDDDD 1px;border-top:none;background:white;padding:5px 12px 5px 12px&quot;&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;date(yyyymmdd)&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;border-top:none;border-left:none;border-bottom:solid #DDDDDD 1px;border-right:solid #DDDDDD 1px;background:white;padding:5px 12px 5px 12px&quot;&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;容纳日期。&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;h3 style=&quot;margin-top:19px;margin-right:0;margin-bottom:13px;margin-left: 0&quot;&gt;&lt;a&gt;MySQL &lt;/a&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;数据类型&lt;/span&gt;&lt;/h3&gt;&lt;p style=&quot;margin-bottom:13px&quot;&gt;&lt;span style=&quot;font-family:宋体&quot;&gt;在&lt;/spa