INSERT INTO `pa_topic` VALUES ('33', '36', '4', '4iD1bJm7rE3ueaHTsYgnBvVUPR6Zjx', '121.229.180.134', '【4】配置applicationContext-dao.xml', '1553657962', '1553657962', '&lt;p&gt;本节操作完成了对spring父容器中dao层扫描加载及其分页的配置。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;1、在resources中创建MyBatis-Config.xml&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;具体操作如下图所示：&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190327/1553657908552802.gif&quot; title=&quot;1553657908552802.gif&quot; alt=&quot;1553657908552802.gif&quot; width=&quot;800&quot; height=&quot;532&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;2、修改MyBatis-Config.xml&lt;/strong&gt;&lt;/p&gt;&lt;pre class=&quot;brush:xml;toolbar:false&quot;&gt;&amp;lt;?xml&amp;nbsp;version=&amp;quot;1.0&amp;quot;&amp;nbsp;encoding=&amp;quot;UTF-8&amp;quot;&amp;nbsp;?&amp;gt;\n&amp;lt;!DOCTYPE&amp;nbsp;configuration\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;PUBLIC&amp;nbsp;&amp;quot;-//mybatis.org//DTD&amp;nbsp;Config&amp;nbsp;3.0//EN&amp;quot;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;quot;http://mybatis.org/dtd/mybatis-3-config.dtd&amp;quot;&amp;gt;\n&amp;lt;configuration&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;plugins&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;plugin&amp;nbsp;interceptor=&amp;quot;com.github.pagehelper.PageInterceptor&amp;quot;&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/plugin&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/plugins&amp;gt;\n&amp;lt;/configuration&amp;gt;&lt;/pre&gt;&lt;p&gt;&lt;strong&gt;&amp;nbsp;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;3、创建applicationContext-dao.xmll&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;具体操作如下图所示：&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190327/1553657923541877.gif&quot; title=&quot;1553657923541877.gif&quot; alt=&quot;1553657923541877.gif&quot; width=&quot;800&quot; height=&quot;532&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;4、修改applicationContext-dao.xml&lt;/strong&gt;&lt;/p&gt;&lt;pre class=&quot;brush:xml;toolbar:false&quot;&gt;&amp;lt;?xml&amp;nbsp;version=&amp;quot;1.0&amp;quot;&amp;nbsp;encoding=&amp;quot;UTF-8&amp;quot;?&amp;gt;\n&amp;lt;beans&amp;nbsp;xmlns=&amp;quot;http://www.springframework.org/schema/beans&amp;quot;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;xmlns:xsi=&amp;quot;http://www.w3.org/2001/XMLSchema-instance&amp;quot;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;xsi:schemaLocation=&amp;quot;http://www.springframework.org/schema/beans&amp;nbsp;http://www.springframework.org/schema/beans/spring-beans.xsd&amp;quot;&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;!--配置数据库连接池，使用的是druid--&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;bean&amp;nbsp;id=&amp;quot;dataSource&amp;quot;&amp;nbsp;class=&amp;quot;com.alibaba.druid.pool.DruidDataSource&amp;quot;&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;property&amp;nbsp;name=&amp;quot;driverClassName&amp;quot;&amp;nbsp;value=&amp;quot;com.mysql.jdbc.Driver&amp;quot;&amp;gt;&amp;lt;/property&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;property&amp;nbsp;name=&amp;quot;url&amp;quot;&amp;nbsp;value=&amp;quot;jdbc:mysql://localhost:3306/student&amp;quot;&amp;gt;&amp;lt;/property&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;property&amp;nbsp;name=&amp;quot;username&amp;quot;&amp;nbsp;value=&amp;quot;root&amp;quot;&amp;gt;&amp;lt;/property&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;property&amp;nbsp;name=&amp;quot;password&amp;quot;&amp;nbsp;value=&amp;quot;zhangli&amp;quot;&amp;gt;&amp;lt;/property&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/bean&amp;gt;\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;!--配置SqlSession的工厂--&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;bean&amp;nbsp;id=&amp;quot;sqlSessionFactory&amp;quot;&amp;nbsp;class=&amp;quot;org.mybatis.spring.SqlSessionFactoryBean&amp;quot;&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;property&amp;nbsp;name=&amp;quot;configLocation&amp;quot;&amp;nbsp;&amp;nbsp;value=&amp;quot;classpath:Mybatis-Config.xml&amp;quot;/&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;property&amp;nbsp;name=&amp;quot;dataSource&amp;quot;&amp;nbsp;ref=&amp;quot;dataSource&amp;quot;&amp;gt;&amp;lt;/property&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/bean&amp;gt;\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;!--配置数据库操作的接口以及xml所在package--&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;bean&amp;nbsp;class=&amp;quot;org.mybatis.spring.mapper.MapperScannerConfigurer&amp;quot;&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;property&amp;nbsp;name=&amp;quot;basePackage&amp;quot;&amp;nbsp;value=&amp;quot;net.wanho.mapper&amp;quot;&amp;gt;&amp;lt;/property&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/bean&amp;gt;\n&amp;lt;/beans&amp;gt;&lt;/pre&gt;&lt;p&gt;至此，我们完成了spring父容器中dao层扫描加载及其分页的配置。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '5', '0', '0', '0', '0', '0', '0');
INSERT INTO `pa_topic` VALUES ('34', '36', '4', 'EMTGyK4riBpehglb2oFqJP9ftwCaxL', '121.229.180.134', '【5】配置applicationContext-trans.xml', '1553658499', '1553658499', '&lt;p&gt;本节操作完成了对spring父容器中事务层扫描加载的配置。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;1、在resources中创建applicationContext-trans.xml&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;具体操作如下图所示：&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190327/1553658230474310.gif&quot; title=&quot;1553658230474310.gif&quot; alt=&quot;1553658230474310.gif&quot; width=&quot;800&quot; height=&quot;532&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;2、修改applicationContext-trans.xml&lt;/strong&gt;&lt;/p&gt;&lt;pre class=&quot;brush:xml;toolbar:false&quot;&gt;&amp;lt;?xml&amp;nbsp;version=&amp;quot;1.0&amp;quot;&amp;nbsp;encoding=&amp;quot;UTF-8&amp;quot;?&amp;gt;\n&amp;lt;beans&amp;nbsp;xmlns=&amp;quot;http://www.springframework.org/schema/beans&amp;quot;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;xmlns:xsi=&amp;quot;http://www.w3.org/2001/XMLSchema-instance&amp;quot;&amp;nbsp;xmlns:aop=&amp;quot;http://www.springframework.org/schema/aop&amp;quot;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;xmlns:tx=&amp;quot;http://www.springframework.org/schema/tx&amp;quot;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;xsi:schemaLocation=&amp;quot;http://www.springframework.org/schema/beans&amp;nbsp;http://www.springframework.org/schema/beans/spring-beans.xsd&amp;nbsp;http://www.springframework.org/schema/aop&amp;nbsp;http://www.springframework.org/schema/aop/spring-aop.xsd&amp;nbsp;http://www.springframework.org/schema/tx&amp;nbsp;http://www.springframework.org/schema/tx/spring-tx.xsd&amp;quot;&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;!--设置事务管理器，引用的是applicationContext-dao.xml中配置的datasource--&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;bean&amp;nbsp;id=&amp;quot;transactionManager&amp;quot;&amp;nbsp;class=&amp;quot;org.springframework.jdbc.datasource.DataSourceTransactionManager&amp;quot;&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;property&amp;nbsp;name=&amp;quot;dataSource&amp;quot;&amp;nbsp;ref=&amp;quot;dataSource&amp;quot;&amp;gt;&amp;lt;/property&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/bean&amp;gt;\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;!--配置事务通知，引用上面设置的事务管理器\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;add、delete、update开头的方法需要事务\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;其他方法都是只读的\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;--&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;tx:advice&amp;nbsp;id=&amp;quot;advice&amp;quot;&amp;nbsp;transaction-manager=&amp;quot;transactionManager&amp;quot;&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;tx:attributes&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;tx:method&amp;nbsp;name=&amp;quot;add*&amp;quot;&amp;nbsp;propagation=&amp;quot;REQUIRED&amp;quot;/&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;tx:method&amp;nbsp;name=&amp;quot;update*&amp;quot;&amp;nbsp;propagation=&amp;quot;REQUIRED&amp;quot;/&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;tx:method&amp;nbsp;name=&amp;quot;delete*&amp;quot;&amp;nbsp;propagation=&amp;quot;REQUIRED&amp;quot;/&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;tx:method&amp;nbsp;name=&amp;quot;*&amp;quot;&amp;nbsp;read-only=&amp;quot;true&amp;quot;/&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/tx:attributes&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/tx:advice&amp;gt;\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;!--定义连接点，advisor定义了通知在连接点上执行即织入--&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;aop:config&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;aop:pointcut&amp;nbsp;id=&amp;quot;pointcut&amp;quot;&amp;nbsp;expression=&amp;quot;execution(*&amp;nbsp;net.wanho.service.*.*(..))&amp;quot;&amp;gt;&amp;lt;/aop:pointcut&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;aop:advisor&amp;nbsp;advice-ref=&amp;quot;advice&amp;quot;&amp;nbsp;pointcut-ref=&amp;quot;pointcut&amp;quot;&amp;gt;&amp;lt;/aop:advisor&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/aop:config&amp;gt;\n&amp;lt;/beans&amp;gt;&lt;/pre&gt;&lt;p&gt;至此，我们完成了对spring父容器中事务层扫描加载的配置。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '7', '0', '0', '0', '0', '0', '0');
INSERT INTO `pa_topic` VALUES ('35', '36', '4', 'ap8cqndsjzLZNMbHi10tYxkV9BAfmP', '121.229.180.134', '【6】配置web.xml', '1553669216', '1553669216', '&lt;p&gt;本节操作完成了在web.xml中对父子容器的配置。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;1、修改web.xml&lt;/strong&gt;&lt;/p&gt;&lt;pre class=&quot;brush:xml;toolbar:false&quot;&gt;&amp;lt;!DOCTYPE&amp;nbsp;web-app&amp;nbsp;PUBLIC\n&amp;nbsp;&amp;quot;-//Sun&amp;nbsp;Microsystems,&amp;nbsp;Inc.//DTD&amp;nbsp;Web&amp;nbsp;Application&amp;nbsp;2.3//EN&amp;quot;\n&amp;nbsp;&amp;quot;http://java.sun.com/dtd/web-app_2_3.dtd&amp;quot;&amp;nbsp;&amp;gt;\n&amp;nbsp;\n&amp;lt;web-app&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;lt;display-name&amp;gt;Archetype&amp;nbsp;Created&amp;nbsp;Web&amp;nbsp;Application&amp;lt;/display-name&amp;gt;\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;lt;!--该节点定义了spring父容器所要读取的文件，applicationContext-*.xml表明所有以&amp;quot;applicationContext-&amp;quot;都会被加载--&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;lt;context-param&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;param-name&amp;gt;contextConfigLocation&amp;lt;/param-name&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;param-value&amp;gt;classpath:applicationContext-*.xml&amp;lt;/param-value&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;lt;/context-param&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;lt;listener&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;listener-class&amp;gt;org.springframework.web.context.ContextLoaderListener&amp;lt;/listener-class&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;lt;/listener&amp;gt;\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;lt;!--init-param节点定义了spring子容器所要读取的文件，load-on-startup保证了系统启动之时就会加载该servlet--&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;lt;servlet&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;servlet-name&amp;gt;mvc&amp;lt;/servlet-name&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;servlet-class&amp;gt;org.springframework.web.servlet.DispatcherServlet&amp;lt;/servlet-class&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;init-param&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;param-name&amp;gt;contextConfigLocation&amp;lt;/param-name&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;param-value&amp;gt;classpath:springmvc.xml&amp;lt;/param-value&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/init-param&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;load-on-startup&amp;gt;1&amp;lt;/load-on-startup&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;lt;/servlet&amp;gt;\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;lt;!--url-pattern节点的&amp;quot;/&amp;quot;取值，保证了所有的url都会被拦截--&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;lt;servlet-mapping&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;servlet-name&amp;gt;mvc&amp;lt;/servlet-name&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;url-pattern&amp;gt;/&amp;lt;/url-pattern&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;lt;/servlet-mapping&amp;gt;\n&amp;lt;/web-app&amp;gt;&lt;/pre&gt;&lt;p&gt;父容器即spring容器，ioc、aop皆在于此；子容器即springmvc容器，mvc在这里实现。父容器先加载，子容器后加载。子容器能访问父容器，父容器不能访问子容器。&lt;/p&gt;&lt;p&gt;至此，我们完成了在web.xml中父子容器的配置。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2', '0', '0', '0', '0', '0', '0');
INSERT INTO `pa_topic` VALUES ('36', '36', '4', 'Nipn5BrU0aW91L7yJEm6SohOP4wIqt', '121.229.180.134', '【7】完成新增学生信息api', '1553670031', '1553670031', '&lt;p&gt;本节操作将完成新增用户的api，并进行测试。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;1、前提约束&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;安装好mysql数据库&lt;/p&gt;&lt;p&gt;数据库具有student database&lt;/p&gt;&lt;p&gt;账号root，密码zhangli【请根据实际情况确定】&lt;/p&gt;&lt;p&gt;在student database中有t_student表&lt;/p&gt;&lt;p&gt;t_student表结构为 ( id int, name varchar(20))，且主键自增&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;2、创建net.wanho.entity.Student.java类，与t_stu对应，即实体类&lt;/strong&gt;&lt;/p&gt;&lt;pre class=&quot;brush:java;toolbar:false&quot;&gt;package&amp;nbsp;net.wanho.entity;\n&amp;nbsp;\nimport&amp;nbsp;java.io.Serializable;\n&amp;nbsp;\npublic&amp;nbsp;class&amp;nbsp;Student&amp;nbsp;implements&amp;nbsp;Serializable&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;private&amp;nbsp;int&amp;nbsp;id;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;private&amp;nbsp;String&amp;nbsp;name;\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;Student()&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;Student(int&amp;nbsp;id,&amp;nbsp;String&amp;nbsp;name)&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;this.id&amp;nbsp;=&amp;nbsp;id;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;this.name&amp;nbsp;=&amp;nbsp;name;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;int&amp;nbsp;getId()&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;id;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;void&amp;nbsp;setId(int&amp;nbsp;id)&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;this.id&amp;nbsp;=&amp;nbsp;id;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;String&amp;nbsp;getName()&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;name;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;void&amp;nbsp;setName(String&amp;nbsp;name)&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;this.name&amp;nbsp;=&amp;nbsp;name;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;@Override\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;String&amp;nbsp;toString()&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;&amp;quot;Student{&amp;quot;&amp;nbsp;+\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;quot;id=&amp;quot;&amp;nbsp;+&amp;nbsp;id&amp;nbsp;+\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;quot;,&amp;nbsp;name=&amp;#39;&amp;quot;&amp;nbsp;+&amp;nbsp;name&amp;nbsp;+&amp;nbsp;&amp;#39;\\&amp;#39;&amp;#39;&amp;nbsp;+\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;}&amp;#39;;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n}&lt;/pre&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;3、创建net.wanho.mapper.StudentMapper.java接口。&lt;/strong&gt;&lt;/p&gt;&lt;pre class=&quot;brush:java;toolbar:false&quot;&gt;package&amp;nbsp;net.wanho.mapper;\n&amp;nbsp;\nimport&amp;nbsp;net.wanho.entity.Student;\n&amp;nbsp;\npublic&amp;nbsp;interface&amp;nbsp;StudentMapper&amp;nbsp;{\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;void&amp;nbsp;add(Student&amp;nbsp;student)&amp;nbsp;throws&amp;nbsp;Exception;\n}&lt;/pre&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;br/&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;4、创建net/wanho/mapper/StudentMapper.xml【此文件应与上述接口同名】。&lt;/strong&gt;&lt;/p&gt;&lt;pre class=&quot;brush:java;toolbar:false&quot;&gt;&amp;lt;?xml&amp;nbsp;version=&amp;quot;1.0&amp;quot;&amp;nbsp;encoding=&amp;quot;UTF-8&amp;quot;&amp;nbsp;?&amp;gt;\n&amp;lt;!DOCTYPE&amp;nbsp;mapper\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;PUBLIC&amp;nbsp;&amp;quot;-//mybatis.org//DTD&amp;nbsp;Mapper&amp;nbsp;3.0//EN&amp;quot;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;quot;http://mybatis.org/dtd/mybatis-3-mapper.dtd&amp;quot;&amp;gt;\n&amp;lt;mapper&amp;nbsp;namespace=&amp;quot;net.wanho.mapper.StudentMapper&amp;quot;&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;!--id的值与接口中方法名同名，parameterType即方法的参数类型，\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;useGeneratedKeys即使用自增主键，keyProperty定义了主键--&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;insert&amp;nbsp;id=&amp;quot;add&amp;quot;&amp;nbsp;parameterType=&amp;quot;net.wanho.entity.Student&amp;quot;&amp;nbsp;useGeneratedKeys=&amp;quot;true&amp;quot;&amp;nbsp;keyProperty=&amp;quot;id&amp;quot;&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;insert&amp;nbsp;into&amp;nbsp;t_student(name)&amp;nbsp;values(#{name})\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/insert&amp;gt;\n&amp;lt;/mapper&amp;gt;&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;5、创建net.wanho.service.StudentServiceI.java接口&lt;/strong&gt;&lt;/p&gt;&lt;pre class=&quot;brush:java;toolbar:false&quot;&gt;package&amp;nbsp;net.wanho.service;\n&amp;nbsp;\nimport&amp;nbsp;net.wanho.entity.Student;\n&amp;nbsp;\npublic&amp;nbsp;interface&amp;nbsp;StudentServiceI&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;void&amp;nbsp;addStudent(Student&amp;nbsp;student)throws&amp;nbsp;Exception;\n}&lt;/pre&gt;&lt;p&gt;&lt;strong&gt;&lt;br/&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;6、创建net.wanho.service.impl.StudentServiceImpl.java，即上述接口的实现类&lt;/strong&gt;&lt;/p&gt;&lt;pre class=&quot;brush:java;toolbar:false&quot;&gt;package&amp;nbsp;net.wanho.service.impl;\n&amp;nbsp;\nimport&amp;nbsp;net.wanho.entity.Student;\nimport&amp;nbsp;net.wanho.mapper.StudentMapper;\nimport&amp;nbsp;net.wanho.service.StudentServiceI;\nimport&amp;nbsp;org.springframework.stereotype.Service;\n&amp;nbsp;\nimport&amp;nbsp;javax.annotation.Resource;\n&amp;nbsp;\n@Service\npublic&amp;nbsp;class&amp;nbsp;StudentServiceImpl&amp;nbsp;implements&amp;nbsp;StudentServiceI&amp;nbsp;{\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//注入StudentMapper\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;@Resource\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;private&amp;nbsp;StudentMapper&amp;nbsp;studentMapper;\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;void&amp;nbsp;addStudent(Student&amp;nbsp;student)&amp;nbsp;throws&amp;nbsp;Exception&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;studentMapper.add(student);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;7、创建net.wanho.controller.StudentController.java，即api的入口类&lt;/strong&gt;&lt;/p&gt;&lt;pre class=&quot;brush:java;toolbar:false&quot;&gt;package&amp;nbsp;net.wanho.controller;\n&amp;nbsp;\nimport&amp;nbsp;com.alibaba.fastjson.JSONObject;\nimport&amp;nbsp;net.wanho.entity.Student;\nimport&amp;nbsp;net.wanho.service.StudentServiceI;\nimport&amp;nbsp;org.springframework.stereotype.Controller;\nimport&amp;nbsp;org.springframework.web.bind.annotation.RequestMapping;\nimport&amp;nbsp;org.springframework.web.bind.annotation.RequestMethod;\nimport&amp;nbsp;org.springframework.web.bind.annotation.ResponseBody;\n&amp;nbsp;\nimport&amp;nbsp;javax.annotation.Resource;\n&amp;nbsp;\n@Controller\npublic&amp;nbsp;class&amp;nbsp;StudentController&amp;nbsp;{\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;@Resource\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;private&amp;nbsp;StudentServiceI&amp;nbsp;studentService;\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;@RequestMapping(value=&amp;quot;/student/add/{name}&amp;quot;,method&amp;nbsp;=&amp;nbsp;RequestMethod.GET)\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;@ResponseBody\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;JSONObject&amp;nbsp;addStudent(Student&amp;nbsp;student)\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;JSONObject&amp;nbsp;ret&amp;nbsp;=&amp;nbsp;new&amp;nbsp;JSONObject();\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;try&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;studentService.addStudent(student);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ret.put(&amp;quot;status&amp;quot;,200);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ret.put(&amp;quot;msg&amp;quot;,&amp;quot;add&amp;nbsp;success:&amp;quot;+student.getId());\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;catch(Exception&amp;nbsp;e)\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ret.put(&amp;quot;status&amp;quot;,100);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ret.put(&amp;quot;msg&amp;quot;,&amp;quot;add&amp;nbsp;error&amp;quot;);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;e.printStackTrace();\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;ret;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n}&lt;/pre&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;br/&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;8、测试1&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;打开浏览器输入http://localhost:8088/student/add/ali，反复执行，会看到返回的主键一直在自增。具体操作如下图所示：&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190327/1553669961940682.gif&quot; title=&quot;1553669961940682.gif&quot; alt=&quot;1553669961940682.gif&quot; width=&quot;800&quot; height=&quot;490&quot;/&gt;&lt;/p&gt;&lt;p&gt;（测试新增学生api）&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;也可以采用如下方式查看是否成功。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;9、测试2&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;打开mysql命令行，查看表中数据，具体操作如下图所示：&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190327/1553669999653482.gif&quot; title=&quot;1553669999653482.gif&quot; alt=&quot;1553669999653482.gif&quot; width=&quot;800&quot; height=&quot;381&quot;/&gt;&lt;/p&gt;&lt;p&gt;（mysql命令行查看数据是否插入）&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;至此，我们完成了新增学生的api，并且完成了测试。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1', '0', '0', '0', '0', '0', '0');
INSERT INTO `pa_topic` VALUES ('37', '36', '4', '6bz89VZQlwAgPMq5FUTndCfH0yJhjL', '58.212.167.3', '【8】完成根据id获取学生信息api', '1553675936', '1553675936', '&lt;p&gt;&lt;strong&gt;1、前提约束&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;完成新增学生api&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;2、修改net.wanho.mapper.StudentMapper.java接口&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;新增get方法申明：&lt;/p&gt;&lt;pre class=&quot;brush:java;toolbar:false&quot;&gt;package&amp;nbsp;net.wanho.mapper;\n&amp;nbsp;\nimport&amp;nbsp;net.wanho.entity.Student;\n&amp;nbsp;\npublic&amp;nbsp;interface&amp;nbsp;StudentMapper&amp;nbsp;{\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;void&amp;nbsp;add(Student&amp;nbsp;student)&amp;nbsp;throws&amp;nbsp;Exception;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;void&amp;nbsp;get(int&amp;nbsp;id)&amp;nbsp;throws&amp;nbsp;Exception;\n}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;3、修改net/wanho/mapper/StudentMapper.xml文件&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;新增get方法对应的sql：&lt;/p&gt;&lt;pre class=&quot;brush:xml;toolbar:false&quot;&gt;&amp;lt;?xml&amp;nbsp;version=&amp;quot;1.0&amp;quot;&amp;nbsp;encoding=&amp;quot;UTF-8&amp;quot;&amp;nbsp;?&amp;gt;\n&amp;lt;!DOCTYPE&amp;nbsp;mapper\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;PUBLIC&amp;nbsp;&amp;quot;-//mybatis.org//DTD&amp;nbsp;Mapper&amp;nbsp;3.0//EN&amp;quot;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;quot;http://mybatis.org/dtd/mybatis-3-mapper.dtd&amp;quot;&amp;gt;\n&amp;lt;mapper&amp;nbsp;namespace=&amp;quot;net.wanho.mapper.StudentMapper&amp;quot;&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;!--id的值与接口中方法名同名，parameterType即方法的参数类型，\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;useGeneratedKeys即使用自增主键，keyProperty定义了主键--&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;insert&amp;nbsp;id=&amp;quot;add&amp;quot;&amp;nbsp;parameterType=&amp;quot;net.wanho.entity.Student&amp;quot;&amp;nbsp;useGeneratedKeys=&amp;quot;true&amp;quot;&amp;nbsp;keyProperty=&amp;quot;id&amp;quot;&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;insert&amp;nbsp;into&amp;nbsp;t_student(name)&amp;nbsp;values(#{name})\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/insert&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;select&amp;nbsp;id=&amp;quot;get&amp;quot;&amp;nbsp;parameterType=&amp;quot;int&amp;quot;&amp;nbsp;resultType=&amp;quot;net.wanho.entity.Student&amp;quot;&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;select&amp;nbsp;*&amp;nbsp;from&amp;nbsp;t_student&amp;nbsp;where&amp;nbsp;id=#{id}\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/select&amp;nbsp;&amp;gt;\n&amp;lt;/mapper&amp;gt;&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;4、修改net.wanho.service.StudentServiceI.java接口&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;新增getStudent方法申明：&lt;/p&gt;&lt;pre class=&quot;brush:java;toolbar:false&quot;&gt;package&amp;nbsp;net.wanho.service;\n&amp;nbsp;\nimport&amp;nbsp;net.wanho.entity.Student;\n&amp;nbsp;\npublic&amp;nbsp;interface&amp;nbsp;StudentServiceI&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;void&amp;nbsp;addStudent(Student&amp;nbsp;student)throws&amp;nbsp;Exception;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Student&amp;nbsp;getStudent(int&amp;nbsp;id)throws&amp;nbsp;Exception;\n}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;5、修改net.wanho.service.impl.StudentServiceImpl.java&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;新增getStudent方法的实现：&lt;/p&gt;&lt;pre class=&quot;brush:java;toolbar:false&quot;&gt;package&amp;nbsp;net.wanho.service.impl;\n&amp;nbsp;\nimport&amp;nbsp;net.wanho.entity.Student;\nimport&amp;nbsp;net.wanho.mapper.StudentMapper;\nimport&amp;nbsp;net.wanho.service.StudentServiceI;\nimport&amp;nbsp;org.springframework.stereotype.Service;\n&amp;nbsp;\nimport&amp;nbsp;javax.annotation.Resource;\n&amp;nbsp;\n@Service\npublic&amp;nbsp;class&amp;nbsp;StudentServiceImpl&amp;nbsp;implements&amp;nbsp;StudentServiceI&amp;nbsp;{\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//注入StudentMapper\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;@Resource\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;private&amp;nbsp;StudentMapper&amp;nbsp;studentMapper;\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;void&amp;nbsp;addStudent(Student&amp;nbsp;student)&amp;nbsp;throws&amp;nbsp;Exception&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;studentMapper.add(student);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;Student&amp;nbsp;getStudent(int&amp;nbsp;id)&amp;nbsp;throws&amp;nbsp;Exception&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;studentMapper.get(id);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n}&lt;/pre&gt;&lt;p&gt;&lt;strong&gt;&lt;br/&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;6、修改net.wanho.controller.StudentController.java&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;创建getStudent API入口：&lt;/p&gt;&lt;pre class=&quot;brush:java;toolbar:false&quot;&gt;package&amp;nbsp;net.wanho.controller;\n&amp;nbsp;\nimport&amp;nbsp;com.alibaba.fastjson.JSONObject;\nimport&amp;nbsp;net.wanho.entity.Student;\nimport&amp;nbsp;net.wanho.service.StudentServiceI;\nimport&amp;nbsp;org.springframework.stereotype.Controller;\nimport&amp;nbsp;org.springframework.web.bind.annotation.RequestMapping;\nimport&amp;nbsp;org.springframework.web.bind.annotation.RequestMethod;\nimport&amp;nbsp;org.springframework.web.bind.annotation.ResponseBody;\n&amp;nbsp;\nimport&amp;nbsp;javax.annotation.Resource;\n&amp;nbsp;\n@Controller\npublic&amp;nbsp;class&amp;nbsp;StudentController&amp;nbsp;{\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;@Resource\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;private&amp;nbsp;StudentServiceI&amp;nbsp;studentService;\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;@RequestMapping(value=&amp;quot;/student/add/{name}&amp;quot;,method&amp;nbsp;=&amp;nbsp;RequestMethod.GET)\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;@ResponseBody\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;JSONObject&amp;nbsp;addStudent(Student&amp;nbsp;student)\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;JSONObject&amp;nbsp;ret&amp;nbsp;=&amp;nbsp;new&amp;nbsp;JSONObject();\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;try&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;studentService.addStudent(student);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ret.put(&amp;quot;status&amp;quot;,200);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ret.put(&amp;quot;status&amp;quot;,200);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ret.put(&amp;quot;msg&amp;quot;,&amp;quot;add&amp;nbsp;success:&amp;quot;+student.getId());\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;catch(Exception&amp;nbsp;e)\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ret.put(&amp;quot;status&amp;quot;,100);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ret.put(&amp;quot;msg&amp;quot;,&amp;quot;add&amp;nbsp;error&amp;quot;);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;e.printStackTrace();\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;ret;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;/**\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;以url带参的方式传id到后台，需要使用PathVariable获取\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;id\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*&amp;nbsp;@return\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;*/\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;@RequestMapping(value=&amp;quot;/student/get/{id}&amp;quot;,method&amp;nbsp;=&amp;nbsp;RequestMethod.GET)\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;@ResponseBody\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;JSONObject&amp;nbsp;getStudent(@PathVariable(&amp;quot;id&amp;quot;)&amp;nbsp;int&amp;nbsp;id)\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;JSONObject&amp;nbsp;ret&amp;nbsp;=&amp;nbsp;new&amp;nbsp;JSONObject();\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;try&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;Student&amp;nbsp;student&amp;nbsp;=&amp;nbsp;studentService.getStudent(id);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ret.put(&amp;quot;status&amp;quot;,200);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ret.put(&amp;quot;data&amp;quot;,student);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;catch(Exception&amp;nbsp;e)\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ret.put(&amp;quot;status&amp;quot;,100);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ret.put(&amp;quot;msg&amp;quot;,&amp;quot;get&amp;nbsp;error&amp;quot;);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;e.printStackTrace();\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;ret;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;7、测试&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;打开mysql命令行以及浏览器，具体操作如下图所示：&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190327/1553673460653598.gif&quot; title=&quot;1553673460653598.gif&quot; alt=&quot;1553673460653598.gif&quot; width=&quot;800&quot; height=&quot;492&quot;/&gt;&lt;/p&gt;&lt;p&gt;（测试使用id获取用户信息）&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;至此，我们完成了根据id获取学生信息的api及其测试。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2', '0', '0', '0', '0', '0', '0');
