INSERT INTO `pa_topic` VALUES ('44', '37', '4', 'OKDl4ovTWItzL5hUcpjwiYGrkMX8Cn', '58.212.167.3', '【2】springboot支持jsp', '1554198981', '1554198981', '&lt;p&gt;springboot建议使用模板技术如freemarker、thymleaf，不建议使用jsp，但是它支持使用jsp。本文将阐述springboot中如何支持jsp。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;1、环境约束&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;win10 64位操作系统&lt;/p&gt;&lt;p&gt;idea2018.1.5&lt;/p&gt;&lt;p&gt;maven-3.0.5&lt;/p&gt;&lt;p&gt;jdk-8u162-windows-x64&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;2、前提约束&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;完成springboot创建web项目 https://www.jianshu.com/p/de979f53ad80&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;3、修改pom.xml&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;加入依赖如下：&lt;/p&gt;&lt;pre class=&quot;brush:xml;toolbar:false&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;lt;dependency&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;groupId&amp;gt;org.apache.tomcat.embed&amp;lt;/groupId&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;artifactId&amp;gt;tomcat-embed-jasper&amp;lt;/artifactId&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/dependency&amp;gt;\n加入resources如下：\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;resources&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;resource&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;directory&amp;gt;src/main/java&amp;lt;/directory&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;includes&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;include&amp;gt;**/*.xml&amp;lt;/include&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/includes&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/resource&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;resource&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;directory&amp;gt;src/main/resources&amp;lt;/directory&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;includes&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;include&amp;gt;**/*.*&amp;lt;/include&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/includes&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/resource&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;resource&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;directory&amp;gt;src/main/webapp&amp;lt;/directory&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;includes&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;include&amp;gt;**/**&amp;lt;/include&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/includes&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/resource&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/resources&amp;gt;&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;4、在src/main当中创建webapp，并加入项目&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;具体操作如下：&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190402/1554198912944456.gif&quot; title=&quot;1554198912944456.gif&quot; alt=&quot;1554198912944456.gif&quot; width=&quot;800&quot; height=&quot;438&quot;/&gt;&lt;/p&gt;&lt;p&gt;创建webapp&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;5、在src/main/webapp下创建index.jsp&lt;/strong&gt;&lt;/p&gt;&lt;pre class=&quot;brush:xml;toolbar:false&quot;&gt;&amp;lt;%@&amp;nbsp;page&amp;nbsp;contentType=&amp;quot;text/html;charset=UTF-8&amp;quot;&amp;nbsp;language=&amp;quot;java&amp;quot;&amp;nbsp;%&amp;gt;\n&amp;lt;html&amp;gt;\n&amp;lt;head&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;title&amp;gt;Title&amp;lt;/title&amp;gt;\n&amp;lt;/head&amp;gt;\n&amp;lt;body&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;this&amp;nbsp;is&amp;nbsp;jsp&amp;nbsp;page&amp;nbsp;in&amp;nbsp;webapp\n&amp;lt;/body&amp;gt;\n&amp;lt;/html&amp;gt;&lt;/pre&gt;&lt;p&gt;&lt;strong&gt;&lt;br/&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;6、启动项目&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190402/1554198948460090.gif&quot; title=&quot;1554198948460090.gif&quot; alt=&quot;1554198948460090.gif&quot; width=&quot;800&quot; height=&quot;436&quot;/&gt;&lt;/p&gt;&lt;p&gt;启动项目&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;7、测试&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;打开浏览器，输入http://localhost:8888/index.jsp。具体操作如下：&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190402/1554198963873092.gif&quot; title=&quot;1554198963873092.gif&quot; alt=&quot;1554198963873092.gif&quot; width=&quot;800&quot; height=&quot;498&quot;/&gt;&lt;/p&gt;&lt;p&gt;测试&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;至此，我们完成了在springboot中支持jsp页面，并完成了测试。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '2', '0', '0', '0', '0', '0', '0');
INSERT INTO `pa_topic` VALUES ('45', '37', '4', 'Xvft4GsaoTzOwbFYJ2qINCeLcVBljE', '58.212.167.3', '【3】springboot支持mybatis', '1554199185', '1554199185', '&lt;p&gt;&lt;strong&gt;1、环境约束&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;win10 64位操作系统&lt;/p&gt;&lt;p&gt;idea2018.1.5&lt;/p&gt;&lt;p&gt;maven-3.0.5&lt;/p&gt;&lt;p&gt;jdk-8u162-windows-x64&lt;/p&gt;&lt;p&gt;mysql6.5&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;2、前提约束&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;完成springboot创建web项目 https://www.jianshu.com/p/de979f53ad80&lt;/p&gt;&lt;p&gt;注意：笔者创建项目的时候约束的包前缀是net.wanho.springboot.springbootweb，读者可以自行创建包名，只是要注意本文中的代码也要修改包名&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;3、修改pom.xml&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;在dependencies标签中加入以下依赖：&lt;/p&gt;&lt;pre class=&quot;brush:xml;toolbar:false&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;lt;dependency&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;groupId&amp;gt;org.mybatis.spring.boot&amp;lt;/groupId&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;artifactId&amp;gt;mybatis-spring-boot-starter&amp;lt;/artifactId&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;version&amp;gt;1.3.2&amp;lt;/version&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/dependency&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;dependency&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;groupId&amp;gt;mysql&amp;lt;/groupId&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;artifactId&amp;gt;mysql-connector-java&amp;lt;/artifactId&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;version&amp;gt;5.1.47&amp;lt;/version&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/dependency&amp;gt;&lt;/pre&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;br/&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;4、在主入口类同等路径下分别创建controller、service、mapper、entity包&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;在entity中加入User.java&lt;/p&gt;&lt;pre class=&quot;brush:java;toolbar:false&quot;&gt;package&amp;nbsp;net.wanho.springboot.springbootweb.entity;\npublic&amp;nbsp;class&amp;nbsp;User&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;private&amp;nbsp;int&amp;nbsp;id;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;private&amp;nbsp;String&amp;nbsp;name;\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;User(int&amp;nbsp;id,&amp;nbsp;String&amp;nbsp;name)&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;this.id&amp;nbsp;=&amp;nbsp;id;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;this.name&amp;nbsp;=&amp;nbsp;name;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;User()&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;int&amp;nbsp;getId()&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;id;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;void&amp;nbsp;setId(int&amp;nbsp;id)&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;this.id&amp;nbsp;=&amp;nbsp;id;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;String&amp;nbsp;getName()&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;name;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;void&amp;nbsp;setName(String&amp;nbsp;name)&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;this.name&amp;nbsp;=&amp;nbsp;name;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n}\n在mapper中加入UserMapper.java\npackage&amp;nbsp;net.wanho.springboot.springbootweb.mapper;\n&amp;nbsp;\nimport&amp;nbsp;net.wanho.springboot.springbootweb.entity.User;\n&amp;nbsp;\nimport&amp;nbsp;java.util.List;\n&amp;nbsp;\npublic&amp;nbsp;interface&amp;nbsp;UserMapper&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;List&amp;lt;User&amp;gt;&amp;nbsp;query();\n}\n在service中加入UserService.java\npackage&amp;nbsp;net.wanho.springboot.springbootweb.service;\n&amp;nbsp;\nimport&amp;nbsp;net.wanho.springboot.springbootweb.entity.User;\nimport&amp;nbsp;net.wanho.springboot.springbootweb.mapper.UserMapper;\nimport&amp;nbsp;org.springframework.stereotype.Service;\n&amp;nbsp;\nimport&amp;nbsp;javax.annotation.Resource;\nimport&amp;nbsp;java.util.List;\n&amp;nbsp;\n@Service\npublic&amp;nbsp;class&amp;nbsp;UserService&amp;nbsp;{\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;@Resource\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;private&amp;nbsp;UserMapper&amp;nbsp;userMapper;\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;List&amp;lt;User&amp;gt;&amp;nbsp;queryUsers()\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;userMapper.query();\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n}\n在controller中加入UserController.java\npackage&amp;nbsp;net.wanho.springboot.springbootweb.controller;\n&amp;nbsp;\nimport&amp;nbsp;net.wanho.springboot.springbootweb.entity.User;\nimport&amp;nbsp;net.wanho.springboot.springbootweb.service.UserService;\nimport&amp;nbsp;org.springframework.stereotype.Controller;\nimport&amp;nbsp;org.springframework.web.bind.annotation.RequestMapping;\nimport&amp;nbsp;org.springframework.web.bind.annotation.ResponseBody;\n&amp;nbsp;\nimport&amp;nbsp;javax.annotation.Resource;\nimport&amp;nbsp;java.util.List;\n&amp;nbsp;\n@Controller\npublic&amp;nbsp;class&amp;nbsp;UserController&amp;nbsp;{\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;@Resource\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;private&amp;nbsp;UserService&amp;nbsp;userService;\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;@RequestMapping(&amp;quot;/user/query&amp;quot;)\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;@ResponseBody\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;List&amp;lt;User&amp;gt;&amp;nbsp;queryUsers()&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;userService.queryUsers();\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;5、在resources下创建一个mapper文件夹&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;在该文件下加入UserMapper.xml&lt;/p&gt;&lt;pre class=&quot;brush:xml;toolbar:false;&quot;&gt;&amp;lt;?xml&amp;nbsp;version=&amp;quot;1.0&amp;quot;&amp;nbsp;encoding=&amp;quot;UTF-8&amp;quot;&amp;nbsp;?&amp;gt;\n&amp;lt;!DOCTYPE&amp;nbsp;mapper&amp;nbsp;PUBLIC&amp;nbsp;&amp;quot;-//mybatis.org//DTD&amp;nbsp;Mapper&amp;nbsp;3.0//EN&amp;quot;&amp;nbsp;&amp;quot;http://mybatis.org/dtd/mybatis-3-mapper.dtd&amp;quot;&amp;nbsp;&amp;gt;\n&amp;lt;mapper&amp;nbsp;namespace=&amp;quot;net.wanho.springboot.springbootweb.mapper.UserMapper&amp;quot;&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;select&amp;nbsp;id=&amp;quot;query&amp;quot;&amp;nbsp;resultType=&amp;quot;net.wanho.springboot.springbootweb.entity.User&amp;quot;&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;select&amp;nbsp;*&amp;nbsp;from&amp;nbsp;t_user\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/select&amp;gt;\n&amp;lt;/mapper&amp;gt;&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;6、修改application.properties&lt;/strong&gt;&lt;/p&gt;&lt;pre class=&quot;brush:xml;toolbar:false&quot;&gt;spring.datasource.driver-class-name=com.mysql.jdbc.Driver\nspring.datasource.url=jdbc:mysql://localhost:3306/test\nspring.datasource.username=root\n#笔者的数据库密码是zhangli,请读者根据自己数据库密码修改\nspring.datasource.password=zhangli\nmybatis.mapper-locations=classpath:mapper/*.xml&lt;/pre&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;br/&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;7、登录mysql数据库，创建表并完成初始化&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;执行以下sql脚本&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;create&amp;nbsp;database&amp;nbsp;test;\nuse&amp;nbsp;test;\ncreate&amp;nbsp;table&amp;nbsp;t_user(id&amp;nbsp;int&amp;nbsp;,&amp;nbsp;name&amp;nbsp;varchar(20));\ninsert&amp;nbsp;into&amp;nbsp;t_user(id,name)&amp;nbsp;values(1,&amp;#39;ali&amp;#39;);\ninsert&amp;nbsp;into&amp;nbsp;t_user(id,name)&amp;nbsp;values(2,&amp;#39;zhangli&amp;#39;);\ninsert&amp;nbsp;into&amp;nbsp;t_user(id,name)&amp;nbsp;values(3,&amp;#39;xiaoli&amp;#39;);&lt;/pre&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;8、启动并测试&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;在浏览器中输入http://localhost:8080/user/query，回车查看结果。具体操作如下：&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190402/1554199166126329.gif&quot; title=&quot;1554199166126329.gif&quot; alt=&quot;1554199166126329.gif&quot; width=&quot;800&quot; height=&quot;612&quot;/&gt;&lt;/p&gt;&lt;p&gt;测试&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;至此，我们完成了springboot对mybatis的支持，并完成了测试。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '3', '0', '0', '0', '0', '0', '0');
INSERT INTO `pa_topic` VALUES ('46', '37', '4', 'F3DKhenjtmXf0cEYTZz9l68brPiHgx', '58.212.167.3', '【4】springboot支持hibernate', '1554199613', '1554199613', '&lt;p&gt;&lt;strong&gt;1、环境约束&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;win10 64位操作系统&lt;/p&gt;&lt;p&gt;idea2018.1.5&lt;/p&gt;&lt;p&gt;maven-3.0.5&lt;/p&gt;&lt;p&gt;jdk-8u162-windows-x64&lt;/p&gt;&lt;p&gt;mysql6.5&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;2、前提约束&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;完成springboot创建web项目 https://www.jianshu.com/p/de979f53ad80&lt;/p&gt;&lt;p&gt;注意：笔者创建项目的时候约束的包前缀是net.wanho.springboot.hibernatedemo，读者可以自行创建包名，只是要注意本文中的代码也要修改包名&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;3、修改pom.xml&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;在dependencies标签中加入以下依赖：&lt;/p&gt;&lt;pre class=&quot;brush:xml;toolbar:false&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;lt;dependency&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;groupId&amp;gt;org.springframework.boot&amp;lt;/groupId&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;artifactId&amp;gt;spring-boot-starter-data-jpa&amp;lt;/artifactId&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/dependency&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;dependency&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;groupId&amp;gt;mysql&amp;lt;/groupId&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;artifactId&amp;gt;mysql-connector-java&amp;lt;/artifactId&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;version&amp;gt;5.1.47&amp;lt;/version&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/dependency&amp;gt;&lt;/pre&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;br/&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;4、在主入口类同等路径下分别创建entity、dao、service、controller包&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;在entity中加入User.java&lt;/p&gt;&lt;pre class=&quot;brush:java;toolbar:false&quot;&gt;package&amp;nbsp;net.wanho.springboot.hibernatedemo.entity;\n&amp;nbsp;\n&amp;nbsp;\nimport&amp;nbsp;javax.persistence.*;\nimport&amp;nbsp;java.io.Serializable;\n&amp;nbsp;\n@Entity\n@Table(name&amp;nbsp;=&amp;nbsp;&amp;quot;t_user&amp;quot;)\npublic&amp;nbsp;class&amp;nbsp;User&amp;nbsp;implements&amp;nbsp;Serializable&amp;nbsp;{\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;@Id\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;@GeneratedValue(strategy&amp;nbsp;=&amp;nbsp;GenerationType.AUTO)\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;private&amp;nbsp;int&amp;nbsp;id;\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;@Column(name&amp;nbsp;=&amp;nbsp;&amp;quot;name&amp;quot;)\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;private&amp;nbsp;String&amp;nbsp;name;\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;User(){\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;User(String&amp;nbsp;name)&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;this.name&amp;nbsp;=&amp;nbsp;name;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;int&amp;nbsp;getId()&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;id;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;void&amp;nbsp;setId(int&amp;nbsp;id)&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;this.id&amp;nbsp;=&amp;nbsp;id;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;String&amp;nbsp;getName()&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;name;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;void&amp;nbsp;setName(String&amp;nbsp;name)&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;this.name&amp;nbsp;=&amp;nbsp;name;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n}\n在dao中加入UserDao.java\npackage&amp;nbsp;net.wanho.springboot.hibernatedemo.dao;\n&amp;nbsp;\nimport&amp;nbsp;net.wanho.springboot.hibernatedemo.entity.User;\nimport&amp;nbsp;org.springframework.data.jpa.repository.JpaRepository;\nimport&amp;nbsp;org.springframework.stereotype.Repository;\n&amp;nbsp;\n@Repository\npublic&amp;nbsp;interface&amp;nbsp;UserDao&amp;nbsp;extends&amp;nbsp;JpaRepository&amp;lt;User,&amp;nbsp;Integer&amp;gt;&amp;nbsp;{\n&amp;nbsp;\n}\n在service中加入UserService.java\npackage&amp;nbsp;net.wanho.springboot.hibernatedemo.service;\n&amp;nbsp;\nimport&amp;nbsp;net.wanho.springboot.hibernatedemo.dao.UserDao;\nimport&amp;nbsp;net.wanho.springboot.hibernatedemo.entity.User;\nimport&amp;nbsp;org.springframework.stereotype.Service;\n&amp;nbsp;\nimport&amp;nbsp;javax.annotation.Resource;\n&amp;nbsp;\n@Service\npublic&amp;nbsp;class&amp;nbsp;UserService&amp;nbsp;{\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;@Resource\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;private&amp;nbsp;UserDao&amp;nbsp;userdao;\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;void&amp;nbsp;insert(User&amp;nbsp;user)\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;userdao.save(user);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;List&amp;lt;User&amp;gt;&amp;nbsp;query()\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;userdao.findAll();\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n}\n在controller中加入UserController.java\npackage&amp;nbsp;net.wanho.springboot.hibernatedemo.controller;\n&amp;nbsp;\nimport&amp;nbsp;net.wanho.springboot.hibernatedemo.entity.User;\nimport&amp;nbsp;net.wanho.springboot.hibernatedemo.service.UserService;\nimport&amp;nbsp;org.springframework.stereotype.Controller;\nimport&amp;nbsp;org.springframework.web.bind.annotation.RequestMapping;\nimport&amp;nbsp;org.springframework.web.bind.annotation.ResponseBody;\n&amp;nbsp;\nimport&amp;nbsp;javax.annotation.Resource;\n&amp;nbsp;\n@Controller\npublic&amp;nbsp;class&amp;nbsp;UserController&amp;nbsp;{\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;@Resource\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;private&amp;nbsp;UserService&amp;nbsp;userService;\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;@RequestMapping(&amp;quot;/insertuser&amp;quot;)\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;@ResponseBody\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;String&amp;nbsp;insertUser(User&amp;nbsp;user)\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;userService.insert(user);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;&amp;quot;insert&amp;nbsp;ok&amp;quot;;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;@RequestMapping(&amp;quot;/queryusers&amp;quot;)\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;@ResponseBody\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;List&amp;lt;User&amp;gt;&amp;nbsp;queryUsers()\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;userService.query();\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n}&lt;/pre&gt;&lt;p&gt;&lt;strong&gt;&lt;br/&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;5、将application.properties改名为application.yml【官网建议使用yml格式，yml格式下键值对为树形结构】&lt;/strong&gt;&lt;/p&gt;&lt;pre class=&quot;brush:java;toolbar:false&quot;&gt;spring:\n&amp;nbsp;&amp;nbsp;datasource:\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;driver-class-name:&amp;nbsp;com.mysql.jdbc.Driver\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;url:&amp;nbsp;jdbc:mysql://localhost:3306/cms\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;username:&amp;nbsp;root\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;#&amp;nbsp;笔者的数据库密码是zhangli,请读者根据自己数据库密码修改\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;password:&amp;nbsp;zhangli\n&amp;nbsp;&amp;nbsp;jpa:\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;database&amp;nbsp;:&amp;nbsp;mysql\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;show-sql&amp;nbsp;:&amp;nbsp;true\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;hibernate.ddl-auto&amp;nbsp;:&amp;nbsp;create\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;hibernate.dialect&amp;nbsp;:&amp;nbsp;org.hibernate.dialect.MySQL5Dialect&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;6、启动并测试。&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;首先新增用户，再查询用户，具体操作如下：&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/ueditor/php/upload/image/20190402/1554199566312258.gif&quot; title=&quot;1554199566312258.gif&quot; alt=&quot;1554199566312258.gif&quot; width=&quot;800&quot; height=&quot;612&quot;/&gt;&lt;/p&gt;&lt;p&gt;测试&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;至此，我们完成了springboot对hibernate的支持，并做了测试。&lt;/p&gt;', '2', '0', '0', '0', '0', '0', '0');
INSERT INTO `pa_topic` VALUES ('47', '37', '4', '1CnWIldXQ90a2pBN5Tw8EqjAgKrsGS', '58.212.167.3', '【5】springboot支持swagger', '1554199786', '1554200148', '&lt;p&gt;swagger是一款流行的restful api展示和测试的插件，本文讲述springboot如何整合swagger。&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;1、环境约束&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;win10 64位操作系统&lt;/p&gt;&lt;p&gt;idea2018.1.5&lt;/p&gt;&lt;p&gt;maven-3.0.5&lt;/p&gt;&lt;p&gt;jdk-8u162-windows-x64&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;2、前提约束&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;完成springboot创建web项目 https://www.jianshu.com/p/de979f53ad80&lt;/p&gt;&lt;p&gt;注意：笔者创建项目的时候约束的包前缀是net.wanho.springboot，读者可以自行创建包名，只是要注意本文中的代码也要修改包名&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;3、修改pom.xml&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;在dependencies标签中加入以下依赖：&lt;/p&gt;&lt;pre class=&quot;brush:xml;toolbar:false&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;lt;dependency&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;groupId&amp;gt;io.springfox&amp;lt;/groupId&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;artifactId&amp;gt;springfox-swagger-ui&amp;lt;/artifactId&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;version&amp;gt;2.4.0&amp;lt;/version&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/dependency&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;dependency&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;groupId&amp;gt;io.springfox&amp;lt;/groupId&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;artifactId&amp;gt;springfox-swagger2&amp;lt;/artifactId&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;version&amp;gt;2.4.0&amp;lt;/version&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/dependency&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;dependency&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;groupId&amp;gt;com.alibaba&amp;lt;/groupId&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;artifactId&amp;gt;fastjson&amp;lt;/artifactId&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;version&amp;gt;1.2.47&amp;lt;/version&amp;gt;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;/dependency&amp;gt;&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;4、在主启动类同等包下面创建pojo、controller、system三个package&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;在pojo下面加入User.java&lt;/p&gt;&lt;pre class=&quot;brush:java;toolbar:false&quot;&gt;package&amp;nbsp;net.wanho.springboot.pojo;\n&amp;nbsp;\npublic&amp;nbsp;class&amp;nbsp;User&amp;nbsp;{\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;private&amp;nbsp;int&amp;nbsp;id;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;private&amp;nbsp;String&amp;nbsp;name;\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;User(String&amp;nbsp;name)&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;this.name&amp;nbsp;=&amp;nbsp;name;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;User(int&amp;nbsp;id,&amp;nbsp;String&amp;nbsp;name)&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;this.id&amp;nbsp;=&amp;nbsp;id;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;this.name&amp;nbsp;=&amp;nbsp;name;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;User()&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;String&amp;nbsp;getName()&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;name;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;void&amp;nbsp;setName(String&amp;nbsp;name)&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;this.name&amp;nbsp;=&amp;nbsp;name;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;int&amp;nbsp;getId()&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;id;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;void&amp;nbsp;setId(int&amp;nbsp;id)&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;this.id&amp;nbsp;=&amp;nbsp;id;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n}\n在controller下面加入UserController.java\npackage&amp;nbsp;net.wanho.springboot.controller;\n&amp;nbsp;\n&amp;nbsp;\nimport&amp;nbsp;com.alibaba.fastjson.JSONObject;\nimport&amp;nbsp;net.wanho.springboot.pojo.User;\nimport&amp;nbsp;org.springframework.web.bind.annotation.PathVariable;\nimport&amp;nbsp;org.springframework.web.bind.annotation.RequestMapping;\nimport&amp;nbsp;org.springframework.web.bind.annotation.RequestMethod;\nimport&amp;nbsp;org.springframework.web.bind.annotation.RestController;\n&amp;nbsp;\n&amp;nbsp;\n/**\n创建restful形式的crud接口，swagger只关注controller层的请求和响应，\n因此，本文中响应数据是固定的，没有去查数据库，也就没有service层\n**/\n@RestController\n@RequestMapping(&amp;quot;/user&amp;quot;)\npublic&amp;nbsp;class&amp;nbsp;UserController&amp;nbsp;{\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;@RequestMapping(value&amp;nbsp;=&amp;nbsp;&amp;quot;/{name}&amp;quot;,method&amp;nbsp;=&amp;nbsp;RequestMethod.POST)\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;JSONObject&amp;nbsp;insert(@PathVariable(&amp;quot;name&amp;quot;)&amp;nbsp;String&amp;nbsp;name)\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;JSONObject&amp;nbsp;jsonObject&amp;nbsp;=&amp;nbsp;new&amp;nbsp;JSONObject();\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;jsonObject.put(&amp;quot;status&amp;quot;,200);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;jsonObject.put(&amp;quot;msg&amp;quot;,&amp;quot;insert&amp;quot;);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;jsonObject;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;@RequestMapping(value&amp;nbsp;=&amp;nbsp;&amp;quot;/{id}&amp;quot;,method&amp;nbsp;=&amp;nbsp;RequestMethod.DELETE)\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;JSONObject&amp;nbsp;delete(@PathVariable(&amp;quot;id&amp;quot;)&amp;nbsp;int&amp;nbsp;id)\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;JSONObject&amp;nbsp;jsonObject&amp;nbsp;=&amp;nbsp;new&amp;nbsp;JSONObject();\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;jsonObject.put(&amp;quot;status&amp;quot;,200);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;jsonObject.put(&amp;quot;msg&amp;quot;,&amp;quot;delete&amp;nbsp;&amp;quot;+id);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;jsonObject;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;@RequestMapping(value&amp;nbsp;=&amp;nbsp;&amp;quot;/{id}/{name}&amp;quot;,method&amp;nbsp;=&amp;nbsp;RequestMethod.PUT)\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;JSONObject&amp;nbsp;update(@PathVariable(&amp;quot;id&amp;quot;)&amp;nbsp;int&amp;nbsp;id,@PathVariable(&amp;quot;name&amp;quot;)&amp;nbsp;String&amp;nbsp;name)\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;JSONObject&amp;nbsp;jsonObject&amp;nbsp;=&amp;nbsp;new&amp;nbsp;JSONObject();\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;jsonObject.put(&amp;quot;status&amp;quot;,200);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;jsonObject.put(&amp;quot;msg&amp;quot;,&amp;quot;update&amp;quot;);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;jsonObject;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;@RequestMapping(value&amp;nbsp;=&amp;nbsp;&amp;quot;/{id}&amp;quot;,method&amp;nbsp;=&amp;nbsp;RequestMethod.GET)\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;JSONObject&amp;nbsp;get(@PathVariable(&amp;quot;id&amp;quot;)&amp;nbsp;int&amp;nbsp;id)\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;JSONObject&amp;nbsp;jsonObject&amp;nbsp;=&amp;nbsp;new&amp;nbsp;JSONObject();\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;jsonObject.put(&amp;quot;status&amp;quot;,200);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;jsonObject.put(&amp;quot;msg&amp;quot;,&amp;quot;get&amp;quot;);\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;jsonObject;\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\n&amp;nbsp;\n}\n在system下加入Swagger2.java\npackage&amp;nbsp;net.wanho.springboot.system;\n&amp;nbsp;\n//swagger2的配置文件，在项目的启动类的同级文件建立\n&amp;nbsp;\nimport&amp;nbsp;org.springframework.context.annotation.Bean;\nimport&amp;nbsp;org.springframework.context.annotation.Configuration;\nimport&amp;nbsp;springfox.documentation.builders.ApiInfoBuilder;\nimport&amp;nbsp;springfox.documentation.builders.PathSelectors;\nimport&amp;nbsp;springfox.documentation.builders.RequestHandlerSelectors;\nimport&amp;nbsp;springfox.documentation.service.ApiInfo;\nimport&amp;nbsp;springfox.documentation.service.Contact;\nimport&amp;nbsp;springfox.documentation.spi.DocumentationType;\nimport&amp;nbsp;springfox.documentation.spring.web.plugins.Docket;\nimport&amp;nbsp;springfox.documentation.swagger2.annotations.EnableSwagger2;\n&amp;nbsp;\n@Configuration\n@EnableSwagger2\npublic&amp;nbsp;class&amp;nbsp;Swagger2&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//swagger2的配置文件，这里可以配置swagger2的一些基本的内容，比如扫描的包等等\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;@Bean\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;Docket&amp;nbsp;createRestApi()&amp;nbsp;{\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;new&amp;nbsp;Docket(DocumentationType.SWAGGER_2)\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;.apiInfo(apiInfo())\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;.select()\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//为当前包路径\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;